// @ts-nocheck
import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from 'next-auth/next';
import { Session } from 'next-auth';
import { authOptions } from '@/lib/auth';

/**
 * GET /api/auth/check
 * Check current authentication status
 */
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions) as Session | null;

    if (session?.user) {
      const user = session.user as any;
      return NextResponse.json({
        success: true,
        data: {
          isAuthenticated: true,
          user: {
            id: session.user.id,
            email: session.user.email,
            name: session.user.name,
            role: user.role,
            isEmailVerified: user.isEmailVerified,
            onboardingCompleted: user.onboardingCompleted,
          }
        },
        timestamp: new Date().toISOString()
      });
    } else {
      return NextResponse.json({
        success: true,
        data: {
          isAuthenticated: false,
          message: 'Not authenticated'
        },
        timestamp: new Date().toISOString()
      });
    }
  } catch (error) {
    console.error('Auth check error:', error);
    return NextResponse.json({
      success: false,
      error: 'Authentication check failed',
      timestamp: new Date().toISOString()
    }, { 
      status: 500 
    });
  }
}

export async function POST(request: NextRequest) {
  try {
    // Placeholder for future POST authentication
    const body = await request.json().catch(() => ({}));

    return NextResponse.json({
      success: true,
      message: 'POST endpoint placeholder - authentication not yet implemented',
      received: body,
      timestamp: new Date().toISOString()
    }, { 
      status: 200 
    });
  } catch (error) {
    console.error('Auth POST error:', error);
    return NextResponse.json({
      success: false,
      error: 'Internal server error',
      timestamp: new Date().toISOString()
    }, { 
      status: 500 
    });
  }
}

// @ts-nocheck
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { MFAService, MFAMethod, MFASetupRequestSchema } from '@/lib/auth/mfa-service';
import { withAuth, AuthenticatedRequest } from '@/lib/auth-middleware';
import { createSuccessResponse, createErrorResponse } from '@/types/api';
import { auditService } from '@/lib/audit/audit-service';
import { AuditEventCategory, AuditOutcome, RiskLevel } from '@/lib/audit/types';
import { convertZodIssuesToValidationErrors } from '@/lib/prisma-helpers';

/**
 * POST /api/auth/mfa/setup
 * Initiate MFA setup for a user
 */
export async function POST(request: NextRequest) {
  try {
    // Authenticate user
    const user = (request as any).user!;
    if (!user) {
      return NextResponse.json(createErrorResponse('Unauthorized'), { status: 401 });
    }

    // Parse and validate request body
    const body = await request.json();
    const validationResult = MFASetupRequestSchema.safeParse(body);

    if (!validationResult.success) {
      return NextResponse.json(
        createErrorResponse('Invalid MFA setup parameters', 'VALIDATION_ERROR'),
        { status: 400 }
      );
    }

    const { method, phoneNumber } = validationResult.data;

    let setupResult;

    switch (method) {
      case MFAMethod.TOTP:
        setupResult = await MFAService.setupTOTP(user.id, user.email || '');
        break;

      case MFAMethod.SMS:
        if (!phoneNumber) {
          return NextResponse.json(
            createErrorResponse('Phone number is required for SMS MFA'),
            { status: 400 }
          );
        }
        setupResult = await MFAService.setupSMS(user.id, user.email || '', phoneNumber);
        break;

      default:
        return NextResponse.json(
          createErrorResponse('Unsupported MFA method'),
          { status: 400 }
        );
    }

    // Log successful MFA setup initiation
    await auditService.logEvent({
      category: AuditEventCategory.MFA_ENABLED,
      action: 'MFA_SETUP_INITIATED',
      outcome: AuditOutcome.SUCCESS,
      riskLevel: RiskLevel.MEDIUM,
      description: `User initiated ${method} MFA setup`,
      userId: user.id ,
      userEmail: user.email,
      userRole: user.role,
      sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
      userAgent: request.headers.get('user-agent'),
      metadata: {
        mfaMethod: method,
        isRequired: MFAService.isMFARequired(user.role),
      },
    });

    return NextResponse.json(
      createSuccessResponse(setupResult, 'MFA setup initiated successfully')
    );

  } catch (error) {
    console.error('MFA setup error:', error);

    // Log the error
    await auditService.logEvent({
      category: AuditEventCategory.MFA_FAILURE,
      action: 'MFA_SETUP_ERROR',
      outcome: AuditOutcome.FAILURE,
      riskLevel: RiskLevel.HIGH,
      description: 'MFA setup failed',
      errorDetails: {
        errorCode: 'MFA_SETUP_ERROR',
        errorMessage: error instanceof Error ? error.message : 'Unknown error',
      },
    });

    return NextResponse.json(
      createErrorResponse('Failed to setup MFA'), 
      { status: 500 }
    );
  }
}

/**
 * GET /api/auth/mfa/setup
 * Get current MFA setup status for user
 */
export async function GET(request: NextRequest) {
  try {
    // Authenticate user
    const user = (request as any).user!;
    if (!user) {
      return NextResponse.json(createErrorResponse('Unauthorized'), { status: 401 });
    }

    // Get MFA status
    const mfaStatus = await MFAService.getMFAStatus(user.id);

    // Log MFA status check
    await auditService.logEvent({
      category: AuditEventCategory.PHI_ACCESS,
      action: 'MFA_STATUS_CHECK',
      outcome: AuditOutcome.SUCCESS,
      riskLevel: RiskLevel.LOW,
      description: 'User checked MFA status',
      userId: user.id ,
      userEmail: user.email,
      userRole: user.role,
      sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
      userAgent: request.headers.get('user-agent'),
      metadata: {
        mfaStatus,
      },
    });

    return NextResponse.json(
      createSuccessResponse(mfaStatus, 'MFA status retrieved successfully')
    );

  } catch (error) {
    console.error('MFA status check error:', error);
    return NextResponse.json(
      createErrorResponse('Failed to retrieve MFA status'), 
      { status: 500 }
    );
  }
}

// @ts-nocheck
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { MFAService, MFAMethod, MFAVerifyRequestSchema } from '@/lib/auth/mfa-service';
import { withAuth, AuthenticatedRequest } from '@/lib/auth-middleware';
import { createSuccessResponse, createErrorResponse } from '@/types/api';
import { auditService } from '@/lib/audit/audit-service';
import { AuditEventCategory, AuditOutcome, RiskLevel } from '@/lib/audit/types';
import { convertZodIssuesToValidationErrors } from '@/lib/prisma-helpers';

/**
 * POST /api/auth/mfa/verify
 * Verify MFA code during setup or authentication
 */
export async function POST(request: NextRequest) {
  try {
    // Parse and validate request body first for better error handling
    const body = await request.json();
    const validationResult = MFAVerifyRequestSchema.safeParse(body);

    if (!validationResult.success) {
      return NextResponse.json(
        createErrorResponse('Invalid MFA verification parameters', 'VALIDATION_ERROR'),
        { status: 400 }
      );
    }

    const { method, code, trustDevice } = validationResult.data;
    const purpose = body.purpose || 'login'; // 'setup' or 'login'

    // Authenticate user
    const user = (request as any).user!;
    if (!user) {
      // Log unauthorized MFA verification attempt
      await auditService.logEvent({
        category: AuditEventCategory.MFA_FAILURE,
        action: 'UNAUTHORIZED_MFA_VERIFICATION',
        outcome: AuditOutcome.FAILURE,
        riskLevel: RiskLevel.HIGH,
        description: 'Unauthorized MFA verification attempt',
        sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
        userAgent: request.headers.get('user-agent'),
        metadata: {
          mfaMethod: method,
          purpose,
        },
      });

      return NextResponse.json(createErrorResponse('Unauthorized'), { status: 401 });
    }

    let verificationResult;

    if (purpose === 'setup') {
      // Verify MFA code during setup process
      verificationResult = await MFAService.verifySetup(user.id, user.email || '', method, code);

      if (verificationResult.success) {
        // Log successful MFA setup completion
        await auditService.logEvent({
          category: AuditEventCategory.MFA_ENABLED,
          action: 'MFA_SETUP_COMPLETED',
          outcome: AuditOutcome.SUCCESS,
          riskLevel: RiskLevel.MEDIUM,
          description: `User completed ${method} MFA setup`,
          userId: user.id ,
          userEmail: user.email,
          userRole: user.role,
          sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
          userAgent: request.headers.get('user-agent'),
          metadata: {
            mfaMethod: method,
            backupCodesGenerated: !!verificationResult.backupCodes,
          },
        });
      } else {
        // Log failed MFA setup verification
        await auditService.logEvent({
          category: AuditEventCategory.MFA_FAILURE,
          action: 'MFA_SETUP_VERIFICATION_FAILED',
          outcome: AuditOutcome.FAILURE,
          riskLevel: RiskLevel.MEDIUM,
          description: `MFA setup verification failed for ${method}`,
          userId: user.id ,
          userEmail: user.email,
          userRole: user.role,
          sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
          userAgent: request.headers.get('user-agent'),
          metadata: {
            mfaMethod: method,
          },
        });
      }
    } else {
      // Verify MFA code during authentication
      verificationResult = await MFAService.verifyMFA(user.id, user.email || '', method, code, trustDevice);

      if (verificationResult.success) {
        // Log successful MFA verification
        await auditService.logEvent({
          category: AuditEventCategory.MFA_SUCCESS,
          action: 'MFA_VERIFICATION_SUCCESS',
          outcome: AuditOutcome.SUCCESS,
          riskLevel: RiskLevel.LOW,
          description: `Successful ${method} MFA verification`,
          userId: user.id ,
          userEmail: user.email,
          userRole: user.role,
          sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
          userAgent: request.headers.get('user-agent'),
          metadata: {
            mfaMethod: method,
            trustDevice: trustDevice,
            trustTokenGenerated: !!verificationResult.trustToken,
          },
        });
      } else {
        // Log failed MFA verification
        await auditService.logEvent({
          category: AuditEventCategory.MFA_FAILURE,
          action: 'MFA_VERIFICATION_FAILED',
          outcome: AuditOutcome.FAILURE,
          riskLevel: RiskLevel.HIGH,
          description: `Failed ${method} MFA verification`,
          userId: user.id ,
          userEmail: user.email,
          userRole: user.role,
          sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
          userAgent: request.headers.get('user-agent'),
          metadata: {
            mfaMethod: method,
          },
        });
      }
    }

    if (!verificationResult.success) {
      return NextResponse.json(
        createErrorResponse('Invalid MFA code', 'MFA_VERIFICATION_FAILED'),
        { status: 400 }
      );
    }

    // Remove sensitive data from response
    const responseData: any = {
      success: verificationResult.success,
    };

    // Handle different verification result types safely
    if ('trustToken' in verificationResult && verificationResult.trustToken) {
      responseData.trustToken = verificationResult.trustToken;
    }

    if (purpose === 'setup' && 'backupCodes' in verificationResult && verificationResult.backupCodes) {
      responseData.backupCodes = verificationResult.backupCodes;
    }

    return NextResponse.json(
      createSuccessResponse(responseData, 'MFA verification successful')
    );

  } catch (error) {
    console.error('MFA verification error:', error);

    // Log the error without user context if authentication failed
    await auditService.logEvent({
      category: AuditEventCategory.MFA_FAILURE,
      action: 'MFA_VERIFICATION_ERROR',
      outcome: AuditOutcome.FAILURE,
      riskLevel: RiskLevel.HIGH,
      description: 'MFA verification system error',
      sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
      userAgent: request.headers.get('user-agent'),
      errorDetails: {
        errorCode: 'MFA_VERIFICATION_ERROR',
        errorMessage: error instanceof Error ? error.message : 'Unknown error',
      },
    });

    return NextResponse.json(
      createErrorResponse('Failed to verify MFA code'), 
      { status: 500 }
    );
  }
}

/**
 * GET /api/auth/mfa/verify
 * Send MFA challenge code (for SMS/Email methods)
 */
export async function GET(request: NextRequest) {
  try {
    // Authenticate user
    const user = (request as any).user!;
    if (!user) {
      return NextResponse.json(createErrorResponse('Unauthorized'), { status: 401 });
    }

    // Get method from query parameters
    const url = new URL(request.url);
    const method = url.searchParams.get('method') as MFAMethod;

    if (!method || !Object.values(MFAMethod).includes(method)) {
      return NextResponse.json(
        createErrorResponse('Invalid or missing MFA method'),
        { status: 400 }
      );
    }

    // Send MFA challenge
    const challengeResult = await MFAService.sendChallenge(user.id, user.email || '', method);

    // Log challenge sent
    await auditService.logEvent({
      category: AuditEventCategory.MFA_SUCCESS,
      action: 'MFA_CHALLENGE_SENT',
      outcome: AuditOutcome.SUCCESS,
      riskLevel: RiskLevel.LOW,
      description: `MFA challenge sent via ${method}`,
      userId: user.id ,
      userEmail: user.email,
      userRole: user.role,
      sourceIp: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip'),
      userAgent: request.headers.get('user-agent'),
      metadata: {
        mfaMethod: method,
        maskedDestination: challengeResult.maskedDestination,
      },
    });

    return NextResponse.json(
      createSuccessResponse(challengeResult, 'MFA challenge sent successfully')
    );

  } catch (error) {
    console.error('MFA challenge error:', error);

    // Log the error
    await auditService.logEvent({
      category: AuditEventCategory.MFA_FAILURE,
      action: 'MFA_CHALLENGE_ERROR',
      outcome: AuditOutcome.FAILURE,
      riskLevel: RiskLevel.MEDIUM,
      description: 'Failed to send MFA challenge',
      errorDetails: {
        errorCode: 'MFA_CHALLENGE_ERROR',
        errorMessage: error instanceof Error ? error.message : 'Unknown error',
      },
    });

    return NextResponse.json(
      createErrorResponse('Failed to send MFA challenge'), 
      { status: 500 }
    );
  }
}

// @ts-nocheck
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import bcrypt from "bcryptjs";
import { z } from "zod";
import { prisma } from "@/lib/prisma";
import { UserRole } from "@/types/enums";
import { EnhancedUserSchemas } from "@/lib/validation/schemas";
import * as crypto from "crypto";

// Use the enhanced registration schema with security validation
const registerSchema = EnhancedUserSchemas.registration;

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const validatedData = registerSchema.parse(body);

    // Check if user already exists
    const existingUser = await prisma.user.findUnique({
      where: { email: validatedData.email.toLowerCase() },
    });

    if (existingUser) {
      return NextResponse.json(
        { error: "User already exists with this email" },
        { status: 400 }
      );
    }

    // Hash password (password is already validated and sanitized by the enhanced schema)
    const hashedPassword = await bcrypt.hash(validatedData.password, 12);

    // Generate verification token
    const verificationToken = crypto.randomBytes(32).toString("hex");

    // Create user (fields are already validated and sanitized by enhanced schema)
    const user = await (prisma.user as any).create({
        data: {
          id: crypto.randomUUID(),
        anonymousId: crypto.randomUUID(),
        email: validatedData.email.toLowerCase(),
        hashedPassword,
        firstName: validatedData.firstName,
        lastName: validatedData.lastName,
        displayName: validatedData.displayName || `${validatedData.firstName} ${validatedData.lastName}`,
        phoneNumber: validatedData.phoneNumber || null,
        role: validatedData.role,
        privacySettings: JSON.stringify({
          shareProfile: false,
          allowDirectMessages: true,
          showOnlineStatus: false,
        }),
        updatedAt: new Date(),
      },
    });

    // Create email verification record
    await (prisma.emailVerification as any).create({
        data: {
          id: crypto.randomUUID(),
        userId: user.id,
        token: verificationToken,
        email: user.email!,
        expires: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours
      },
    });

    // Create appropriate profile based on role
    if (validatedData.role === UserRole.USER) {
      await (prisma.userProfile as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: user.id,
          mentalHealthGoals: [],
          interestedTopics: [],
          preferredCommunication: ["chat"],
          crisisContacts: JSON.stringify([]),
          notificationSettings: JSON.stringify({
            email: true,
            push: true,
            crisis: true,
            appointments: true,
          }),
          updatedAt: new Date(),
        },
      });
    } else if (validatedData.role === UserRole.HELPER || validatedData.role === UserRole.THERAPIST || validatedData.role === UserRole.CRISIS_COUNSELOR) {
      await (prisma.helperProfile as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: user.id,
          specializations: [],
          credentials: JSON.stringify({}),
          experience: "",
          approach: "",
          languages: ["en"],
          availability: JSON.stringify({}),
          updatedAt: new Date(),
        },
      });
    } else if (validatedData.role === UserRole.ADMIN || validatedData.role === UserRole.SUPER_ADMIN) {
      await (prisma.adminProfile as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: user.id,
          adminLevel: validatedData.role === UserRole.SUPER_ADMIN ? "SUPER_ADMIN" : "MODERATOR",
          departments: [],
          permissions: JSON.stringify({}),
          updatedAt: new Date(),
        },
      });
    }

    // Log audit event
    await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
        userId: user.id,
        action: "user_registration",
        resource: "user",
        resourceId: user.id,
        details: {
          role: validatedData.role,
          email: validatedData.email,
        },
        outcome: "success",
      },
    });

    // TODO: Send verification email
    // await sendVerificationEmail(user.email!, verificationToken);

    return NextResponse.json(
      {
        message: "Registration successful",
        user: {
          id: user.id,
          email: user.email,
          name: user.displayName,
          role: user.role,
          requiresVerification: !user.isEmailVerified,
        },
      },
      { status: 201 }
    );

  } catch (error) {
    console.error("Registration error:", error);

    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Validation failed", details: error.issues },
        { status: 400 }
      );
    }

    return NextResponse.json(
      { error: "Registration failed" },
      { status: 500 }
    );
  }
}

// @ts-nocheck
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import bcrypt from "bcryptjs";
import crypto from "crypto";
import { z } from "zod";
import { prisma } from "@/lib/prisma";

const requestResetSchema = z.object({
  email: z.string().email("Invalid email address"),
});

const resetPasswordSchema = z.object({
  token: z.string().min(1, "Reset token is required"),
  password: z.string().min(8, "Password must be at least 8 characters")
    .regex(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]/, 
      "Password must contain uppercase, lowercase, number, and special character"),
});

// Request password reset
export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { action } = body;

    if (action === "request") {
      const { email } = requestResetSchema.parse(body);

      // Find user
      const user = await prisma.user.findUnique({
        where: { email: email.toLowerCase() },
      });

      // Always return success to prevent email enumeration
      if (!user) {
        return NextResponse.json({
          message: "If an account exists with this email, you will receive reset instructions",
        });
      }

      // Generate reset token
      const resetToken = crypto.randomBytes(32).toString("hex");

      // Delete any existing reset tokens
      await prisma.passwordReset.deleteMany({
        where: { userId: user.id },
      });

      // Create new reset token
      await (prisma.passwordReset as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: user.id,
          token: resetToken,
          expires: new Date(Date.now() + 60 * 60 * 1000), // 1 hour
        },
      });

      // Log audit event
      await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: user.id,
          action: "password_reset_requested",
          resource: "user",
          resourceId: user.id,
          outcome: "success",
        },
      });

      // TODO: Send reset email
      // await sendPasswordResetEmail(user.email!, resetToken);

      return NextResponse.json({
        message: "If an account exists with this email, you will receive reset instructions",
      });

    } else if (action === "reset") {
      const { token, password } = resetPasswordSchema.parse(body);

      // Find reset token
      const resetRecord = await prisma.passwordReset.findUnique({
        where: { token },
        include: { User: true },
      });

      if (!resetRecord || resetRecord.used || resetRecord.expires < new Date()) {
        return NextResponse.json(
          { error: "Invalid or expired reset token" },
          { status: 400 }
        );
      }

      // Hash new password
      const hashedPassword = await bcrypt.hash(password, 12);

      // Update user password and mark token as used
      await prisma.$transaction([
        prisma.user.update({
          where: { id: resetRecord.userId },
          data: {
            hashedPassword,
            failedLoginAttempts: 0,
            lockedUntil: null,
          },
        }),
        prisma.passwordReset.update({
          where: { id: resetRecord.id },
          data: { used: true },
        }),
      ]);

      // Log audit event
      await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: resetRecord.userId,
          action: "password_reset_completed",
          resource: "user",
          resourceId: resetRecord.userId,
          outcome: "success",
        },
      });

      return NextResponse.json({
        message: "Password reset successful",
      });

    } else {
      return NextResponse.json(
        { error: "Invalid action" },
        { status: 400 }
      );
    }

  } catch (error) {
    console.error("Password reset error:", error);

    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Validation failed", details: error.issues },
        { status: 400 }
      );
    }

    return NextResponse.json(
      { error: "Password reset failed" },
      { status: 500 }
    );
  }
}

// Verify reset token
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const token = searchParams.get('token');

    if (!token) {
      return NextResponse.json(
        { error: "Reset token is required" },
        { status: 400 }
      );
    }

    const resetRecord = await prisma.passwordReset.findUnique({
      where: { token },
    });

    const isValid = resetRecord && 
                   !resetRecord.used && 
                   resetRecord.expires > new Date();

    return NextResponse.json({
      valid: !!isValid,
      expired: resetRecord ? resetRecord.expires < new Date() : true,
      used: resetRecord?.used || false,
    });

  } catch (error) {
    console.error("Reset token verification error:", error);
    return NextResponse.json(
      { error: "Token verification failed" },
      { status: 500 }
    );
  }
}

