import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/prisma";
import {  createAuditLogData , generatePrismaCreateFields } from "@/lib/prisma-helpers";

export async function POST(request: NextRequest) {
  try {
    const { token } = await request.json();

    if (!token) {
      return NextResponse.json(
        { error: "Verification token is required" },
        { status: 400 }
      );
    }

    // Find verification record
    const verification = await prisma.emailVerification.findUnique({
      where: { token },
      include: { User: true },
    });

    if (!verification) {
      return NextResponse.json(
        { error: "Invalid verification token" },
        { status: 400 }
      );
    }

    if (verification.expires < new Date()) {
      return NextResponse.json(
        { error: "Verification token has expired" },
        { status: 400 }
      );
    }

    if (verification.verified) {
      return NextResponse.json(
        { error: "Email already verified" },
        { status: 400 }
      );
    }

    // Update user and verification record
    await prisma.$transaction([
      prisma.user.update({
        where: { id: verification.userId },
        data: { isEmailVerified: true },
      }),
      prisma.emailVerification.update({
        where: { id: verification.id },
        data: { verified: true },
      }),
    ]);

    // Log audit event
    await (prisma.auditLog as any).create({
      data: createAuditLogData({
        userId: verification.userId,
        action: "email_verified",
        resource: "user",
        resourceId: verification.userId,
        outcome: "success",
      }),
    });

    return NextResponse.json({
      message: "Email verified successfully",
    });

  } catch (error) {
    console.error("Email verification error:", error);
    return NextResponse.json(
      { error: "Email verification failed" },
      { status: 500 }
    );
  }
}

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const token = searchParams.get('token');

    if (!token) {
      return NextResponse.json(
        { error: "Verification token is required" },
        { status: 400 }
      );
    }

    // Find verification record
    const verification = await prisma.emailVerification.findUnique({
      where: { token },
      include: { User: true },
    });

    if (!verification) {
      return NextResponse.json(
        { error: "Invalid verification token" },
        { status: 400 }
      );
    }

    return NextResponse.json({
      valid: verification.expires > new Date() && !verification.verified,
      email: verification.email,
      expired: verification.expires < new Date(),
      alreadyVerified: verification.verified,
    });

  } catch (error) {
    console.error("Email verification check error:", error);
    return NextResponse.json(
      { error: "Verification check failed" },
      { status: 500 }
    );
  }
}

// @ts-nocheck
// Chat rooms API with CRUD operations and real-time support preparation
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import { prisma } from "@/lib/prisma";
import { withAuth, AuthenticatedRequest, withRoles } from "@/lib/auth-middleware-exports";
import { UserRole } from "@/types/prisma";
import { z } from "zod";

// Input validation schemas
const createRoomSchema = z.object({
  name: z.string().min(1).max(100),
  topic: z.string().min(1).max(50),
  description: z.string().min(1).max(500),
  maxParticipants: z.number().int().min(2).max(50).optional().default(20),
  language: z.string().length(2).optional().default("en"),
  rules: z.array(z.string()).optional().default([]),
  settings: z.object({
    allowAnonymous: z.boolean().optional().default(true),
    requireModeration: z.boolean().optional().default(false),
    minTrustScore: z.number().min(0).max(1).optional().default(0),
    autoModerate: z.boolean().optional().default(true),
    allowFileSharing: z.boolean().optional().default(false),
  }).optional().default(() => ({
    allowAnonymous: true,
    requireModeration: false,
    minTrustScore: 0,
    autoModerate: true,
    allowFileSharing: false,
  })),
});

const updateRoomSchema = z.object({
  name: z.string().min(1).max(100).optional(),
  description: z.string().min(1).max(500).optional(),
  maxParticipants: z.number().int().min(2).max(50).optional(),
  rules: z.array(z.string()).optional(),
  settings: z.object({
    allowAnonymous: z.boolean().optional(),
    requireModeration: z.boolean().optional(),
    minTrustScore: z.number().min(0).max(1).optional(),
    autoModerate: z.boolean().optional(),
    allowFileSharing: z.boolean().optional(),
  }).optional(),
  isActive: z.boolean().optional(),
});

const querySchema = z.object({
  page: z.coerce.number().int().positive().optional().default(1),
  limit: z.coerce.number().int().positive().max(50).optional().default(20),
  topic: z.string().optional(),
  language: z.string().optional(),
  isActive: z.coerce.boolean().optional(),
  search: z.string().optional(),
  includeParticipants: z.coerce.boolean().optional().default(false),
});

// GET /api/community/chat-rooms - Get all chat rooms
export async function GET(req: NextRequest) {
  try {
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const params = querySchema.parse({
      page: searchParams.get("page"),
      limit: searchParams.get("limit"),
      topic: searchParams.get("topic"),
      language: searchParams.get("language"),
      isActive: searchParams.get("isActive"),
      search: searchParams.get("search"),
      includeParticipants: searchParams.get("includeParticipants"),
    });

    const skip = (params.page - 1) * params.limit;

    // Build where clause
    const where: any = {};

    if (params.isActive !== undefined) {
      where.isActive = params.isActive;
    } else {
      where.isActive = true; // Default to active rooms only
    }

    if (params.topic) {
      where.topic = params.topic;
    }

    if (params.language) {
      where.language = params.language;
    }

    if (params.search) {
      where.OR = [
        { name: { contains: params.search, mode: "insensitive" } },
        { description: { contains: params.search, mode: "insensitive" } },
      ];
    }

    // Get rooms with optional participant info
    const includeClause: any = {
      _count: {
        select: {
          ChatParticipant: {
            where: { isActive: true },
          },
          ChatMessage: true,
        },
      },
    };

    if (params.includeParticipants) {
      includeClause.ChatParticipant = {
        where: { isActive: true },
        take: 5, // Show first 5 active participants
        select: {
          userId: true,
          joinedAt: true,
        },
      };
    }

    const [rooms, totalCount] = await Promise.all([
      prisma.chatRoom.findMany({
        where,
        skip,
        take: params.limit,
        orderBy: { lastActivity: "desc" },
        include: includeClause,
      }),
      prisma.chatRoom.count({ where }),
    ]);

    // Process rooms for response
    const processedRooms = rooms.map(room => {
      const { ChatParticipant, _count, ...roomData } = room as any;
      return {
        ...roomData,
        participantCount: _count?.ChatParticipant || 0,
        messageCount: _count?.ChatMessage || 0,
        participants: ChatParticipant || undefined,
        isFull: (_count?.ChatParticipant || 0) >= room.maxParticipants,
      };
    });

    return NextResponse.json({
      rooms: processedRooms,
      pagination: {
        page: params.page,
        limit: params.limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / params.limit),
      },
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid query parameters", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error fetching chat rooms:", error);
    return NextResponse.json(
      { error: "Failed to fetch chat rooms" },
      { status: 500 }
    );
  }
}

// POST /api/community/chat-rooms - Create a new chat room (Helper/Admin only)
export const POST = withRoles(
  [UserRole.HELPER, UserRole.THERAPIST, UserRole.CRISIS_COUNSELOR, UserRole.ADMIN, UserRole.SUPER_ADMIN],
  async (req: AuthenticatedRequest) => {
    try {
      const userId = req.user!.id;

      // Parse and validate input
      const body = await (req as any).json();
      const validatedData = createRoomSchema.parse(body);

      // Check if a similar room already exists
      const existingRoom = await prisma.chatRoom.findFirst({
        where: {
          name: validatedData.name,
          topic: validatedData.topic,
          isActive: true,
        },
      });

      if (existingRoom) {
        return NextResponse.json(
          { error: "A similar chat room already exists" },
          { status: 409 }
        );
      }

      // Add default rules based on topic
      const defaultRules = [
        "Be respectful and supportive",
        "No harassment or discrimination",
        "Maintain confidentiality",
        "No spam or promotional content",
        "Crisis situations will be escalated to counselors",
      ];

      // Add topic-specific rules
      const topicRules: Record<string, string[]> = {
        anxiety: ["Share coping strategies respectfully", "Avoid triggering descriptions"],
        depression: ["Focus on support, not medical advice", "Encourage professional help when needed"],
        crisis: ["This is not a replacement for emergency services", "Counselors are monitoring"],
      };

      const allRules = [
        ...defaultRules,
        ...(topicRules[validatedData.topic] || []),
        ...validatedData.rules,
      ];

      // Create the chat room
      const room = await (prisma.chatRoom as any).create({
        data: {
          id: generatePrismaCreateFields().id,
          name: validatedData.name,
          topic: validatedData.topic,
          description: validatedData.description,
          maxParticipants: validatedData.maxParticipants,
          language: validatedData.language,
          rules: allRules,
          settings: validatedData.settings,
          isActive: true,
          lastActivity: new Date(),
          createdAt: new Date(),
          updatedAt: new Date(),
        },
      });

      // Add the creator as the first moderator
      await (prisma.chatModerator as any).create({
        data: {
          id: generatePrismaCreateFields().id,
          roomId: room.id,
          userId,
          permissions: ["manage_participants", "delete_messages", "ban_users"],
        },
      });

      // Log room creation
      await (prisma.auditLog as any).create({
        data: {
          id: generatePrismaCreateFields().id,
          userId,
          action: "create_chat_room",
          resource: "chat_room",
          resourceId: room.id,
          details: {
            name: room.name,
            topic: room.topic,
          },
          outcome: "success",
        },
      });

      return NextResponse.json({
        message: "Chat room created successfully",
        room,
      }, { status: 201 });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return NextResponse.json(
          { error: "Invalid input", details: error.issues },
          { status: 400 }
        );
      }
      console.error("Error creating chat room:", error);
      return NextResponse.json(
        { error: "Failed to create chat room" },
        { status: 500 }
      );
    }
  }
);

// PUT /api/community/chat-rooms - Update a chat room (Moderator only)
export const PUT = withRoles([UserRole.ADMIN, UserRole.SUPER_ADMIN, UserRole.HELPER], async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;
    const userRole = req.user!.role;
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const roomId = searchParams.get("id");

    if (!roomId) {
      return NextResponse.json(
        { error: "Room ID is required" },
        { status: 400 }
      );
    }

    // Check if room exists
    const existingRoom = await prisma.chatRoom.findUnique({
      where: { id: roomId },
    });

    if (!existingRoom) {
      return NextResponse.json(
        { error: "Chat room not found" },
        { status: 404 }
      );
    }

    // Check if user is a moderator or admin
    const isModerator = await prisma.chatModerator.findFirst({
      where: {
        roomId,
        userId,
      },
    });

    const isAdmin = ["ADMIN", "SUPER_ADMIN"].includes(userRole);

    if (!isModerator && !isAdmin) {
      return NextResponse.json(
        { error: "You don't have permission to update this chat room" },
        { status: 403 }
      );
    }

    // Parse and validate input
    const body = await (req as any).json();
    const validatedData = updateRoomSchema.parse(body);

    // Build update data
    const updateData: any = {
      updatedAt: new Date(),
    };

    if (validatedData.name !== undefined) {
      updateData.name = validatedData.name;
    }

    if (validatedData.description !== undefined) {
      updateData.description = validatedData.description;
    }

    if (validatedData.maxParticipants !== undefined) {
      // Check current participant count
      const currentParticipants = await prisma.chatParticipant.count({
        where: {
          roomId,
          isActive: true,
        },
      });

      if (validatedData.maxParticipants < currentParticipants) {
        return NextResponse.json(
          { 
            error: "Cannot reduce max participants below current count",
            currentCount: currentParticipants,
          },
          { status: 400 }
        );
      }

      updateData.maxParticipants = validatedData.maxParticipants;
    }

    if (validatedData.rules !== undefined) {
      updateData.rules = validatedData.rules;
    }

    if (validatedData.settings !== undefined) {
      updateData.settings = {
        ...(existingRoom.settings as object || {}),
        ...validatedData.settings,
      };
    }

    if (validatedData.isActive !== undefined && isAdmin) {
      updateData.isActive = validatedData.isActive;
    }

    // Update the room
    const updatedRoom = await prisma.chatRoom.update({
      where: { id: roomId },
      data: updateData,
    });

    // Log the update
    await (prisma.auditLog as any).create({
        data: {
          id: generatePrismaCreateFields().id,
        userId,
        action: "update_chat_room",
        resource: "chat_room",
        resourceId: roomId,
        details: {
          changes: Object.keys(updateData),
        },
        outcome: "success",
      },
    });

    return NextResponse.json({
      message: "Chat room updated successfully",
      room: updatedRoom,
    }, { status: 200 });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid input", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error updating chat room:", error);
    return NextResponse.json(
      { error: "Failed to update chat room" },
      { status: 500 }
    );
  }
});

// DELETE /api/community/chat-rooms - Delete/deactivate a chat room (Admin only)
export const DELETE = withRoles(
  [UserRole.ADMIN, UserRole.SUPER_ADMIN],
  async (req: AuthenticatedRequest) => {
    try {
      const userId = req.user!.id;
      const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
      const roomId = searchParams.get("id");
      const permanent = searchParams.get("permanent") === "true";

      if (!roomId) {
        return NextResponse.json(
          { error: "Room ID is required" },
          { status: 400 }
        );
      }

      // Check if room exists
      const existingRoom = await prisma.chatRoom.findUnique({
        where: { id: roomId },
        include: {
          _count: {
            select: {
              ChatMessage: true,
              ChatParticipant: {
                where: { isActive: true },
              },
            },
          },
        },
      });

      if (!existingRoom) {
        return NextResponse.json(
          { error: "Chat room not found" },
          { status: 404 }
        );
      }

      // Check for active participants
      if (existingRoom._count.ChatParticipant > 0 && !permanent) {
        return NextResponse.json(
          { 
            error: "Cannot delete room with active participants. Deactivate it instead or use permanent=true",
            activeParticipants: existingRoom._count.ChatParticipant,
          },
          { status: 400 }
        );
      }

      if (permanent) {
        // Permanently delete the room and all related data
        await prisma.$transaction([
          // First remove all participants
          prisma.chatParticipant.deleteMany({
            where: { roomId },
          }),
          // Remove all moderators
          prisma.chatModerator.deleteMany({
            where: { roomId },
          }),
          // Remove all messages
          prisma.chatMessage.deleteMany({
            where: { roomId },
          }),
          // Finally delete the room
          prisma.chatRoom.delete({
            where: { id: roomId },
          }),
        ]);

        // Log the deletion
        await (prisma.auditLog as any).create({
        data: {
          id: generatePrismaCreateFields().id,
            userId,
            action: "delete_chat_room",
            resource: "chat_room",
            resourceId: roomId,
            details: {
              name: existingRoom.name,
              messageCount: existingRoom._count.ChatMessage,
              permanent: true,
            },
            outcome: "success",
          },
        });

        return NextResponse.json({
          message: "Chat room permanently deleted",
        }, { status: 200 });
      } else {
        // Soft delete - just deactivate the room
        const updatedRoom = await prisma.chatRoom.update({
          where: { id: roomId },
          data: {
            isActive: false,
            updatedAt: new Date(),
          },
        });

        // Remove all active participants
        await prisma.chatParticipant.updateMany({
          where: {
            roomId,
            isActive: true,
          },
          data: {
            isActive: false,
            leftAt: new Date(),
          },
        });

        // Log the deactivation
        await (prisma.auditLog as any).create({
        data: {
          id: generatePrismaCreateFields().id,
            userId,
            action: "deactivate_chat_room",
            resource: "chat_room",
            resourceId: roomId,
            details: {
              name: existingRoom.name,
            },
            outcome: "success",
          },
        });

        return NextResponse.json({
          message: "Chat room deactivated successfully",
          room: updatedRoom,
        }, { status: 200 });
      }

    } catch (error) {
      console.error("Error deleting chat room:", error);
      return NextResponse.json(
        { error: "Failed to delete chat room" },
        { status: 500 }
      );
    }
  }
);

// @ts-nocheck
// Community comments API with CRUD operations and moderation
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import { prisma } from "@/lib/prisma";
import { withAuth, AuthenticatedRequest } from "@/lib/auth-middleware";
import { 
  moderateContent, 
  sanitizeContent, 
  checkContentRateLimit,
  updateTrustScore 
} from "@/lib/community/moderation";
import { z } from "zod";

// Input validation schemas
const createCommentSchema = z.object({
  postId: z.string(),
  parentId: z.string().optional(),
  content: z.string().min(1).max(2000),
  isAnonymous: z.boolean().optional().default(true),
});

const updateCommentSchema = z.object({
  content: z.string().min(1).max(2000),
});

const querySchema = z.object({
  postId: z.string().optional(),
  parentId: z.string().optional(),
  page: z.coerce.number().int().positive().optional().default(1),
  limit: z.coerce.number().int().positive().max(100).optional().default(20),
  sortBy: z.enum(["recent", "popular", "oldest"]).optional().default("recent"),
});

// GET /api/community/comments - Get comments for a post or replies to a comment
export async function GET(req: NextRequest) {
  try {
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const params = querySchema.parse({
      postId: searchParams.get("postId"),
      parentId: searchParams.get("parentId"),
      page: searchParams.get("page"),
      limit: searchParams.get("limit"),
      sortBy: searchParams.get("sortBy"),
    });

    if (!params.postId && !params.parentId) {
      return NextResponse.json(
        { error: "Either postId or parentId is required" },
        { status: 400 }
      );
    }

    const skip = (params.page - 1) * params.limit;

    // Build where clause
    const where: any = {
      isDeleted: false,
      isModerated: false,
    };

    if (params.postId) {
      where.postId = params.postId;
      where.parentId = null; // Get top-level comments only
    } else if (params.parentId) {
      where.parentId = params.parentId; // Get replies to a specific comment
    }

    // Build orderBy clause
    let orderBy: any = {};
    switch (params.sortBy) {
      case "popular":
        orderBy = { likeCount: "desc" };
        break;
      case "oldest":
        orderBy = { createdAt: "asc" };
        break;
      case "recent":
      default:
        orderBy = { createdAt: "desc" };
    }

    // Get comments with author info and reply count
    const [comments, totalCount] = await Promise.all([
      prisma.comment.findMany({
        where,
        skip,
        take: params.limit,
        orderBy,
        include: {
          User: {
            select: {
              id: true,
              displayName: true,
              avatarUrl: true,
              AnonymousIdentity: {
                select: {
                  displayName: true,
                  avatar: true,
                  colorTheme: true,
                },
              },
            },
          },
          _count: {
            select: {
              Replies: {
                where: {
                  isDeleted: false,
                  isModerated: false,
                },
              },
            },
          },
        },
      }),
      prisma.comment.count({ where }),
    ]);

    // Process comments for anonymous authors
    const processedComments = comments.map(comment => {
      const { User, _count, ...commentData } = comment;

      const processedComment: any = {
        ...commentData,
        replyCount: _count.Replies,
      };

      if (comment.isAnonymous && User?.AnonymousIdentity) {
        processedComment.author = {
          displayName: User.AnonymousIdentity.displayName,
          avatar: User.AnonymousIdentity.avatar,
          isAnonymous: true,
        };
      } else if (User) {
        processedComment.author = {
          id: User.id,
          displayName: User.displayName,
          avatar: User.avatarUrl,
          isAnonymous: false,
        };
      } else {
        processedComment.author = {
          displayName: "Anonymous User",
          avatar: null,
          isAnonymous: true,
        };
      }

      return processedComment;
    });

    return NextResponse.json({
      comments: processedComments,
      pagination: {
        page: params.page,
        limit: params.limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / params.limit),
      },
    });
  } catch (error) {
    console.error("Error fetching comments:", error);
    return NextResponse.json(
      { error: "Failed to fetch comments" },
      { status: 500 }
    );
  }
}

// POST /api/community/comments - Create a new comment
export const POST = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;

    // Check rate limiting
    if (!checkContentRateLimit(userId, 10)) {
      return NextResponse.json(
        { error: "Rate limit exceeded. Please wait before commenting again." },
        { status: 429 }
      );
    }

    // Parse and validate input
    const body = await (req as any).json();
    const validatedData = createCommentSchema.parse(body);

    // Verify post exists
    const post = await prisma.communityPost.findUnique({
      where: { id: validatedData.postId },
    });

    if (!post) {
      return NextResponse.json(
        { error: "Post not found" },
        { status: 404 }
      );
    }

    // If replying to a comment, verify parent exists
    if (validatedData.parentId) {
      const parentComment = await prisma.comment.findUnique({
        where: { id: validatedData.parentId },
      });

      if (!parentComment || parentComment.postId !== validatedData.postId) {
        return NextResponse.json(
          { error: "Parent comment not found or doesn't belong to this post" },
          { status: 404 }
        );
      }
    }

    // Sanitize content
    const sanitizedContent = sanitizeContent(validatedData.content);

    // Moderate content for safety
    const moderationResult = await moderateContent(
      sanitizedContent,
      userId,
      "comment"
    );

    // Handle crisis detection
    if (moderationResult.crisisDetected && moderationResult.crisisLevel === "high") {
      // Create crisis alert
      await (prisma.safetyAlert as any).create({
        data: {
          id: crypto.randomUUID(),
          type: "crisis_comment",
          severity: "high",
          userId,
          context: `Comment on post: ${post.title}`,
          indicators: moderationResult.reasons,
          handled: false,
          actions: moderationResult.suggestedActions,
        },
      });
    }

    // Block spam and inappropriate content
    if (moderationResult.spamDetected || moderationResult.inappropriateContent) {
      await updateTrustScore(userId, "negative", 0.1);

      return NextResponse.json(
        { 
          error: "Your comment has been flagged for review.",
          reasons: moderationResult.reasons,
        },
        { status: 400 }
      );
    }

    // Create or get anonymous identity if needed
    if (validatedData.isAnonymous) {
      let anonymousIdentity = await prisma.anonymousIdentity.findUnique({
        where: { userId },
      });

      if (!anonymousIdentity) {
        const adjectives = ["Kind", "Supportive", "Thoughtful", "Gentle", "Understanding"];
        const nouns = ["Friend", "Listener", "Helper", "Companion", "Soul"];
        const randomAdjective = adjectives[Math.floor(Math.random() * adjectives.length)];
        const randomNoun = nouns[Math.floor(Math.random() * nouns.length)];
        const randomNumber = Math.floor(Math.random() * 1000);

        anonymousIdentity = await (prisma.anonymousIdentity as any).create({
        data: {
          id: crypto.randomUUID(),
            userId,
            displayName: `${randomAdjective}${randomNoun}${randomNumber}`,
            avatar: `https://api.dicebear.com/7.x/shapes/svg?seed=${userId}`,
            colorTheme: `hsl(${Math.floor(Math.random() * 360)}, 70%, 60%)`,
            trustScore: 0.5,
            badges: [],
            languages: ["en"],
            updatedAt: new Date(),
          },
        });
      }
    }

    // Create the comment
    const comment = await (prisma.comment as any).create({
        data: {
          id: crypto.randomUUID(),
        postId: validatedData.postId,
        parentId: validatedData.parentId,
        authorId: userId,
        content: sanitizedContent,
        isAnonymous: validatedData.isAnonymous,
        isModerated: moderationResult.requiresReview,
        likeCount: 0,
        isDeleted: false,
        updatedAt: new Date(),
      },
      include: {
        User: {
          select: {
            id: true,
            displayName: true,
            avatarUrl: true,
            AnonymousIdentity: {
              select: {
                displayName: true,
                avatar: true,
                colorTheme: true,
              },
            },
          },
        },
      },
    });

    // Update trust score positively for successful comment
    if (!moderationResult.requiresReview) {
      await updateTrustScore(userId, "positive", 0.02);
    }

    // Process comment for response
    const { User, ...commentData } = comment as any;
    const processedComment = validatedData.isAnonymous && User?.AnonymousIdentity
      ? {
          ...commentData,
          author: {
            displayName: User.AnonymousIdentity.displayName,
            avatar: User.AnonymousIdentity.avatar,
            isAnonymous: true,
          },
        }
      : {
          ...commentData,
          author: {
            id: User?.id,
            displayName: User?.displayName,
            avatar: User?.avatarUrl,
            isAnonymous: false,
          },
        };

    // Send response based on moderation status
    if (moderationResult.requiresReview) {
      return NextResponse.json({
        message: "Your comment has been submitted for review.",
        comment: processedComment,
        moderation: {
          status: "pending_review",
          reasons: moderationResult.reasons,
        },
      }, { status: 201 });
    }

    return NextResponse.json({
      message: "Comment created successfully",
      comment: processedComment,
      interventions: moderationResult.crisisDetected ? moderationResult.suggestedActions : [],
    }, { status: 201 });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid input", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error creating comment:", error);
    return NextResponse.json(
      { error: "Failed to create comment" },
      { status: 500 }
    );
  }
});

// PUT /api/community/comments - Update a comment
export const PUT = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const commentId = searchParams.get("id");

    if (!commentId) {
      return NextResponse.json(
        { error: "Comment ID is required" },
        { status: 400 }
      );
    }

    // Check if comment exists and user is the author
    const existingComment = await prisma.comment.findUnique({
      where: { id: commentId },
    });

    if (!existingComment) {
      return NextResponse.json(
        { error: "Comment not found" },
        { status: 404 }
      );
    }

    if (existingComment.isDeleted) {
      return NextResponse.json(
        { error: "Cannot edit deleted comment" },
        { status: 400 }
      );
    }

    if (existingComment.authorId !== userId) {
      return NextResponse.json(
        { error: "You can only edit your own comments" },
        { status: 403 }
      );
    }

    // Parse and validate input
    const body = await (req as any).json();
    const validatedData = updateCommentSchema.parse(body);

    // Sanitize and moderate content
    const sanitizedContent = sanitizeContent(validatedData.content);
    const moderationResult = await moderateContent(sanitizedContent, userId, "comment");

    if (moderationResult.spamDetected || moderationResult.inappropriateContent) {
      return NextResponse.json(
        { 
          error: "Your updates have been flagged for review.",
          reasons: moderationResult.reasons,
        },
        { status: 400 }
      );
    }

    // Update the comment
    const updatedComment = await prisma.comment.update({
      where: { id: commentId },
      data: {
        content: sanitizedContent,
        isModerated: moderationResult.requiresReview,
        updatedAt: new Date(),
      },
    });

    return NextResponse.json({
      message: "Comment updated successfully",
      comment: updatedComment,
    });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid input", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error updating comment:", error);
    return NextResponse.json(
      { error: "Failed to update comment" },
      { status: 500 }
    );
  }
});

// DELETE /api/community/comments - Delete a comment (soft delete)
export const DELETE = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;
    const userRole = req.user!.role;
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const commentId = searchParams.get("id");

    if (!commentId) {
      return NextResponse.json(
        { error: "Comment ID is required" },
        { status: 400 }
      );
    }

    // Check if comment exists
    const existingComment = await prisma.comment.findUnique({
      where: { id: commentId },
      include: {
        _count: {
          select: { Replies: true },
        },
      },
    });

    if (!existingComment) {
      return NextResponse.json(
        { error: "Comment not found" },
        { status: 404 }
      );
    }

    // Check authorization
    const isAuthor = existingComment.authorId === userId;
    const isAdmin = ["ADMIN", "SUPER_ADMIN"].includes(userRole);

    if (!isAuthor && !isAdmin) {
      return NextResponse.json(
        { error: "You don't have permission to delete this comment" },
        { status: 403 }
      );
    }

    // Soft delete the comment (preserve for replies)
    const updatedComment = await prisma.comment.update({
      where: { id: commentId },
      data: {
        isDeleted: true,
        content: "[This comment has been deleted]",
        updatedAt: new Date(),
      },
    });

    // Log moderation action if deleted by admin
    if (isAdmin && !isAuthor) {
      await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
          userId,
          action: "delete_comment",
          resource: "comment",
          resourceId: commentId,
          details: {
            reason: "Admin action",
            originalAuthor: existingComment.authorId,
            hasReplies: existingComment._count.Replies > 0,
          },
          outcome: "success",
        },
      });
    }

    return NextResponse.json({
      message: "Comment deleted successfully",
      preservedForReplies: existingComment._count.Replies > 0,
    });

  } catch (error) {
    console.error("Error deleting comment:", error);
    return NextResponse.json(
      { error: "Failed to delete comment" },
      { status: 500 }
    );
  }
});

// @ts-nocheck
// Personalized community feed API with recommendations and filtering
import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/prisma";
import { withAuth, AuthenticatedRequest } from "@/lib/auth-middleware";
import { z } from "zod";

// Input validation schema
const feedQuerySchema = z.object({
  page: z.coerce.number().int().positive().optional().default(1),
  limit: z.coerce.number().int().positive().max(50).optional().default(20),
  type: z.enum(["personalized", "trending", "recent", "following"]).optional().default("personalized"),
  categories: z.string().optional(), // Comma-separated categories
  timeframe: z.enum(["today", "week", "month", "all"]).optional().default("week"),
});

// Calculate content score for ranking
function calculateContentScore(
  post: any,
  userPreferences: any,
  timeDecayFactor: number = 0.95
): number {
  const now = Date.now();
  const postAge = now - new Date(post.createdAt).getTime();
  const hoursSincePost = postAge / (1000 * 60 * 60);

  // Base score from engagement
  let score = post.likeCount * 2 + post.viewCount * 0.1 + (post._count?.Comments || 0) * 3;

  // Time decay
  score *= Math.pow(timeDecayFactor, hoursSincePost / 24);

  // Category match bonus
  if (userPreferences?.interestedTopics?.includes(post.category)) {
    score *= 1.5;
  }

  // Pinned posts get a boost
  if (post.isPinned) {
    score *= 2;
  }

  // Crisis or support posts get priority
  if (["crisis", "support", "help"].includes(post.category.toLowerCase())) {
    score *= 1.3;
  }

  return score;
}

// GET /api/community/feed - Get personalized community feed
export const GET = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const params = feedQuerySchema.parse({
      page: searchParams.get("page"),
      limit: searchParams.get("limit"),
      type: searchParams.get("type"),
      categories: searchParams.get("categories"),
      timeframe: searchParams.get("timeframe"),
    });

    const skip = (params.page - 1) * params.limit;

    // Get user preferences for personalization
    const userProfile = await prisma.userProfile.findUnique({
      where: { userId },
      select: {
        interestedTopics: true,
        mentalHealthGoals: true,
      },
    });

    // Get user's anonymous identity for engagement tracking
    const anonymousIdentity = await prisma.anonymousIdentity.findUnique({
      where: { userId },
      select: {
        id: true,
        trustScore: true,
      },
    });

    // Build base where clause
    const baseWhere: any = {
      isModerated: false, // Don't show moderated content
    };

    // Apply category filter
    if (params.categories) {
      const categoryList = params.categories.split(",").map(c => c.trim());
      baseWhere.category = { in: categoryList };
    } else if (userProfile?.interestedTopics && userProfile.interestedTopics.length > 0) {
      // Use user's interested topics if no specific categories requested
      baseWhere.category = { in: userProfile.interestedTopics };
    }

    // Apply timeframe filter
    if (params.timeframe !== "all") {
      const timeframeMap = {
        today: 24,
        week: 24 * 7,
        month: 24 * 30,
      };
      const hoursAgo = timeframeMap[params.timeframe as keyof typeof timeframeMap];
      baseWhere.createdAt = {
        gte: new Date(Date.now() - hoursAgo * 60 * 60 * 1000),
      };
    }

    let posts: any[] = [];
    let totalCount = 0;

    switch (params.type) {
      case "personalized":
        // Get posts with engagement metrics
        const allPosts = await prisma.communityPost.findMany({
          where: baseWhere,
          include: {
            User: {
              select: {
                id: true,
                displayName: true,
                avatarUrl: true,
                AnonymousIdentity: {
                  select: {
                    displayName: true,
                    avatar: true,
                    colorTheme: true,
                  },
                },
              },
            },
            _count: {
              select: {
                Comments: {
                  where: { isDeleted: false },
                },
              },
            },
          },
        });

        // Score and sort posts
        const scoredPosts = allPosts.map(post => ({
          ...post,
          score: calculateContentScore(post, userProfile),
        }));

        scoredPosts.sort((a, b) => b.score - a.score);

        // Paginate scored results
        posts = scoredPosts.slice(skip, skip + params.limit);
        totalCount = scoredPosts.length;
        break;

      case "trending":
        // Get trending posts (high engagement in recent timeframe)
        posts = await prisma.communityPost.findMany({
          where: {
            ...baseWhere,
            createdAt: {
              gte: new Date(Date.now() - 48 * 60 * 60 * 1000), // Last 48 hours
            },
          },
          orderBy: [
            { likeCount: "desc" },
            { viewCount: "desc" },
          ],
          skip,
          take: params.limit,
          include: {
            User: {
              select: {
                id: true,
                displayName: true,
                avatarUrl: true,
                AnonymousIdentity: {
                  select: {
                    displayName: true,
                    avatar: true,
                    colorTheme: true,
                  },
                },
              },
            },
            _count: {
              select: {
                Comments: {
                  where: { isDeleted: false },
                },
              },
            },
          },
        });

        totalCount = await prisma.communityPost.count({
          where: {
            ...baseWhere,
            createdAt: {
              gte: new Date(Date.now() - 48 * 60 * 60 * 1000),
            },
          },
        });
        break;

      case "recent":
        // Get most recent posts
        posts = await prisma.communityPost.findMany({
          where: baseWhere,
          orderBy: { createdAt: "desc" },
          skip,
          take: params.limit,
          include: {
            User: {
              select: {
                id: true,
                displayName: true,
                avatarUrl: true,
                AnonymousIdentity: {
                  select: {
                    displayName: true,
                    avatar: true,
                    colorTheme: true,
                  },
                },
              },
            },
            _count: {
              select: {
                Comments: {
                  where: { isDeleted: false },
                },
              },
            },
          },
        });

        totalCount = await prisma.communityPost.count({ where: baseWhere });
        break;

      case "following":
        // Get posts from followed users/topics (placeholder - needs following system)
        // For now, return posts from users with high trust scores
        const trustedUsers = await prisma.anonymousIdentity.findMany({
          where: {
            trustScore: { gte: 0.7 },
          },
          select: { userId: true },
        });

        const trustedUserIds = trustedUsers.map(u => u.userId);

        posts = await prisma.communityPost.findMany({
          where: {
            ...baseWhere,
            authorId: { in: trustedUserIds },
          },
          orderBy: { createdAt: "desc" },
          skip,
          take: params.limit,
          include: {
            User: {
              select: {
                id: true,
                displayName: true,
                avatarUrl: true,
                AnonymousIdentity: {
                  select: {
                    displayName: true,
                    avatar: true,
                    colorTheme: true,
                  },
                },
              },
            },
            _count: {
              select: {
                Comments: {
                  where: { isDeleted: false },
                },
              },
            },
          },
        });

        totalCount = await prisma.communityPost.count({
          where: {
            ...baseWhere,
            authorId: { in: trustedUserIds },
          },
        });
        break;
    }

    // Process posts for anonymous authors
    const processedPosts = posts.map(post => {
      const { User, _count, score, ...postData } = post;

      const processedPost: any = {
        ...postData,
        commentCount: _count?.Comments || 0,
        score: score || 0,
      };

      if (post.isAnonymous && User?.AnonymousIdentity) {
        processedPost.author = {
          displayName: User.AnonymousIdentity.displayName,
          avatar: User.AnonymousIdentity.avatar,
          isAnonymous: true,
        };
      } else if (User) {
        processedPost.author = {
          id: User.id,
          displayName: User.displayName,
          avatar: User.avatarUrl,
          isAnonymous: false,
        };
      } else {
        processedPost.author = {
          displayName: "Anonymous User",
          avatar: null,
          isAnonymous: true,
        };
      }

      return processedPost;
    });

    // Get related content suggestions
    const suggestions = await getContentSuggestions(userId, userProfile);

    // Get active chat rooms related to user interests
    const recommendedRooms = await prisma.chatRoom.findMany({
      where: {
        isActive: true,
        topic: {
          in: userProfile?.interestedTopics || [],
        },
      },
      take: 5,
      orderBy: { lastActivity: "desc" },
      select: {
        id: true,
        name: true,
        topic: true,
        description: true,
        _count: {
          select: {
            ChatParticipant: {
              where: { isActive: true },
            },
          },
        },
      },
    });

    // Get trending topics
    const trendingTopics = await prisma.communityPost.groupBy({
      by: ["category"],
      where: {
        createdAt: {
          gte: new Date(Date.now() - 24 * 60 * 60 * 1000), // Last 24 hours
        },
        isModerated: false,
      },
      _count: {
        category: true,
      },
      orderBy: {
        _count: {
          category: "desc",
        },
      },
      take: 10,
    });

    return NextResponse.json({
      posts: processedPosts,
      pagination: {
        page: params.page,
        limit: params.limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / params.limit),
      },
      suggestions,
      recommendedRooms: recommendedRooms.map(room => ({
        ...room,
        participantCount: room._count.ChatParticipant,
      })),
      trendingTopics: trendingTopics.map(t => ({
        category: t.category,
        count: t._count.category,
      })),
      feedType: params.type,
    });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid query parameters", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error fetching feed:", error);
    return NextResponse.json(
      { error: "Failed to fetch feed" },
      { status: 500 }
    );
  }
});

// Helper function to get content suggestions
async function getContentSuggestions(userId: string, userProfile: any) {
  const suggestions: any = {
    resources: [],
    activities: [],
    groups: [],
  };

  // Suggest resources based on mental health goals
  if (userProfile?.mentalHealthGoals && userProfile.mentalHealthGoals.length > 0) {
    const resourceMap: Record<string, string[]> = {
      anxiety: [
        "Breathing exercises for anxiety relief",
        "Grounding techniques guide",
        "Progressive muscle relaxation",
      ],
      depression: [
        "Daily mood tracking templates",
        "Behavioral activation worksheets",
        "Gratitude journaling prompts",
      ],
      stress: [
        "Stress management techniques",
        "Time management strategies",
        "Mindfulness meditation guides",
      ],
      sleep: [
        "Sleep hygiene checklist",
        "Bedtime routine builder",
        "Sleep tracking tools",
      ],
      relationships: [
        "Communication skills workshops",
        "Boundary setting guides",
        "Conflict resolution strategies",
      ],
    };

    userProfile.mentalHealthGoals.forEach((goal: string) => {
      const resources = resourceMap[goal.toLowerCase()] || [];
      suggestions.resources.push(...resources);
    });
  }

  // Suggest activities based on recent engagement
  const recentPosts = await prisma.communityPost.findMany({
    where: {
      authorId: userId,
    },
    orderBy: { createdAt: "desc" },
    take: 5,
    select: { category: true },
  });

  const categories = [...new Set(recentPosts.map(p => p.category))];

  const activityMap: Record<string, string[]> = {
    anxiety: ["Join anxiety support group", "Try today's breathing exercise"],
    depression: ["Participate in peer support chat", "Complete mood check-in"],
    wellness: ["Join wellness challenge", "Track your progress"],
  };

  categories.forEach(category => {
    const activities = activityMap[category.toLowerCase()] || [];
    suggestions.activities.push(...activities);
  });

  // Suggest support groups
  const groups = await prisma.supportGroup.findMany({
    where: {
      isActive: true,
      topic: {
        in: userProfile?.interestedTopics || [],
      },
    },
    take: 3,
    select: {
      id: true,
      name: true,
      description: true,
      topic: true,
    },
  });

  suggestions.groups = groups;

  return suggestions;
}

// @ts-nocheck
// API Routes for Support Groups
import { NextRequest, NextResponse } from 'next/server';
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import { PrismaClient } from '@prisma/client';
import { getServerSession } from 'next-auth/next';
import { Session } from 'next-auth';
import { authOptions } from "@/lib/auth";
import { z } from 'zod';
import { groupCreationSchema } from '@/types/community';
import * as crypto from 'crypto';

const prisma = new PrismaClient();

// GET: Fetch all support groups
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions) as Session | null;

    if (!session?.user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const searchParams = request.nextUrl.searchParams;
    const topic = searchParams.get('topic');
    const type = searchParams.get('type');
    const privacy = searchParams.get('privacy');

    const where: Record<string, unknown> = {
      isActive: true,
    };

    if (topic && topic !== 'all') {
      where.topic = topic;
    }

    if (type) {
      where.type = type;
    }

    if (privacy) {
      where.privacy = privacy;
    }

    const groups = await prisma.supportGroup.findMany({
      where,
      include: {
        GroupMembership: {
          where: {
            isActive: true,
          },
          select: {
            userId: true,
          },
        },
        _count: {
          select: {
            GroupMembership: {
              where: {
                isActive: true,
              },
            },
          },
        },
      },
      orderBy: {
        createdAt: 'desc',
      },
    });

    // Transform data for frontend
    const transformedGroups = groups.map((group: any) => ({
      ...group,
      currentMembers: group.GroupMembership.map((m: any) => m.userId),
      nextSession: group.sessions[0]?.scheduledAt || null,
      memberCount: group._count.GroupMembership,
    }));

    return NextResponse.json(transformedGroups);
  } catch (error) {
    console.error('Failed to fetch groups:', error);
    return NextResponse.json(
      { error: 'Failed to fetch support groups' },
      { status: 500 }
    );
  }
}

// POST: Create a new support group
export async function POST(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions) as Session | null;

    if (!session?.user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();

    // Validate input
    const validatedData = groupCreationSchema.parse(body);

    // Get user's anonymous identity
    const identity = await prisma.anonymousIdentity.findUnique({
      where: {
        userId: session.user.id!,
      },
    });

    if (!identity) {
      return NextResponse.json(
        { error: 'Anonymous identity not found' },
        { status: 404 }
      );
    }

    // Check trust score requirement
    const trustMetric = await prisma.trustMetric.findUnique({
      where: {
        userId: session.user.id!,
      },
    });

    if (!trustMetric || trustMetric.score < 100) {
      return NextResponse.json(
        { error: 'Insufficient trust score to create groups' },
        { status: 403 }
      );
    }

    // Create the group
    const group = await (prisma.supportGroup as any).create({
        data: {
          id: generatePrismaCreateFields().id,name: validatedData.name,
        topic: validatedData.topic,
        description: validatedData.description,
        maxMembers: validatedData.maxMembers,
        privacy: validatedData.privacy,
        type: body.type || 'peer_support',
        facilitatorId: session.user.id!,
        schedule: body.schedule || {},
        requirements: body.requirements || {
          languages: ['en'],
          minTrustScore: 0,
        },
        resources: [],
        tags: body.tags || [],
        GroupMembership: {
          create: {
            id: crypto.randomUUID(),
            userId: identity.id,
            role: 'facilitator',
          },
        },
      },
      include: {
        GroupMembership: true,
      },
    });

    // Update trust score for creating a group
    await prisma.trustMetric.update({
      where: {
        userId: session.user.id!,
      },
      data: {
        score: {
          increment: 20,
        },
        history: {
          push: {
            type: 'positive',
            description: 'Created support group',
            impact: 20,
            timestamp: new Date(),
          },
        },
        lastUpdated: new Date(),
      },
    });

    return NextResponse.json(group, { status: 201 });
  } catch (error: unknown) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: 'Invalid input', details: (error as any).issues },
        { status: 400 }
      );
    }

    console.error('Failed to create group:', error);
    return NextResponse.json(
      { error: 'Failed to create support group' },
      { status: 500 }
    );
  }
}

