// @ts-nocheck
// Community moderation API with safety features and crisis intervention
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import { prisma } from "@/lib/prisma";
import { withAuth, AuthenticatedRequest, withRoles } from '@/lib/auth-middleware-exports';
import { UserRole } from "@/types/prisma";
import { 
  moderateContent, 
  isUserRestricted,
  updateTrustScore 
} from "@/lib/community/moderation";
import { z } from "zod";

// Input validation schemas
const moderationActionSchema = z.object({
  type: z.enum(["warn", "mute", "ban", "suspend", "restrict", "approve", "reject"]),
  targetUserId: z.string(),
  reason: z.string().min(1).max(500),
  evidence: z.array(z.string()).optional().default([]),
  duration: z.number().int().positive().optional(), // Duration in hours
  appealable: z.boolean().optional().default(true),
  contentId: z.string().optional(), // ID of the content being moderated
  contentType: z.enum(["post", "comment", "message", "profile"]).optional(),
});

const reviewContentSchema = z.object({
  contentId: z.string(),
  contentType: z.enum(["post", "comment", "message"]),
  action: z.enum(["approve", "reject", "edit"]),
  editedContent: z.string().optional(),
  reason: z.string().optional(),
});

const querySchema = z.object({
  page: z.coerce.number().int().positive().optional().default(1),
  limit: z.coerce.number().int().positive().max(100).optional().default(20),
  type: z.enum(["pending", "resolved", "appeals", "crisis"]).optional().default("pending"),
  userId: z.string().optional(),
  moderatorId: z.string().optional(),
});

// GET /api/community/moderation - Get moderation queue and actions
export const GET = withRoles(
  [UserRole.ADMIN, UserRole.SUPER_ADMIN, UserRole.HELPER, UserRole.THERAPIST],
  async (req: AuthenticatedRequest) => {
    try {
      const moderatorId = req.user!.id;
      const moderatorRole = req.user!.role;
      const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
      const params = querySchema.parse({
        page: searchParams.get("page"),
        limit: searchParams.get("limit"),
        type: searchParams.get("type"),
        userId: searchParams.get("userId"),
        moderatorId: searchParams.get("moderatorId"),
      });

      const skip = (params.page - 1) * params.limit;

      if (params.type === "pending") {
        // Get content pending moderation
        const [posts, comments, safetyAlerts] = await Promise.all([
          // Moderated posts
          prisma.communityPost.findMany({
            where: { isModerated: true },
            skip,
            take: params.limit,
            orderBy: { createdAt: "desc" },
            include: {
              User: {
                select: {
                  id: true,
                  displayName: true,
                  AnonymousIdentity: {
                    select: { displayName: true },
                  },
                },
              },
            },
          }),
          // Moderated comments
          prisma.comment.findMany({
            where: { isModerated: true },
            take: 10,
            orderBy: { createdAt: "desc" },
            include: {
              User: {
                select: {
                  id: true,
                  displayName: true,
                  AnonymousIdentity: {
                    select: { displayName: true },
                  },
                },
              },
            },
          }),
          // Unhandled safety alerts
          prisma.safetyAlert.findMany({
            where: { 
              handled: false,
              severity: { in: ["high", "critical"] },
            },
            take: 5,
            orderBy: { detectedAt: "desc" },
          }),
        ]);

        return NextResponse.json({
          pendingModeration: {
            posts,
            comments,
            safetyAlerts,
          },
          counts: {
            posts: posts.length,
            comments: comments.length,
            alerts: safetyAlerts.length,
          },
        }, { status: 200 });

      } else if (params.type === "resolved") {
        // Get recent moderation actions
        const where: any = {};

        if (params.userId) {
          where.targetUserId = params.userId;
        }

        if (params.moderatorId && moderatorRole === UserRole.SUPER_ADMIN) {
          where.moderatorId = params.moderatorId;
        } else if (moderatorRole !== UserRole.SUPER_ADMIN) {
          where.moderatorId = moderatorId; // Non-admins can only see their own actions
        }

        const actions = await prisma.moderationAction.findMany({
          where,
          skip,
          take: params.limit,
          orderBy: { createdAt: "desc" },
          include: {
            AnonymousIdentity_ModerationAction_targetUserIdToAnonymousIdentity: {
              select: {
                displayName: true,
                userId: true,
              },
            },
            AnonymousIdentity_ModerationAction_moderatorIdToAnonymousIdentity: {
              select: {
                displayName: true,
              },
            },
          },
        });

        const totalCount = await prisma.moderationAction.count({ where });

        return NextResponse.json({
          actions,
          pagination: {
            page: params.page,
            limit: params.limit,
            total: totalCount,
            totalPages: Math.ceil(totalCount / params.limit),
          },
        });

      } else if (params.type === "appeals") {
        // Get appealed moderation actions
        const appeals = await prisma.moderationAction.findMany({
          where: {
            appealed: true,
            appealable: true,
          },
          skip,
          take: params.limit,
          orderBy: { createdAt: "desc" },
          include: {
            AnonymousIdentity_ModerationAction_targetUserIdToAnonymousIdentity: {
              select: {
                displayName: true,
                userId: true,
              },
            },
          },
        });

        return NextResponse.json({
          appeals,
          count: appeals.length,
        }, { status: 200 });

      } else if (params.type === "crisis") {
        // Get crisis-related content and alerts
        const [crisisReports, safetyAlerts] = await Promise.all([
          prisma.crisisReport.findMany({
            where: { resolved: false },
            skip,
            take: params.limit,
            orderBy: { createdAt: "desc" },
            include: {
              User: {
                select: {
                  id: true,
                  displayName: true,
                },
              },
            },
          }),
          prisma.safetyAlert.findMany({
            where: {
              handled: false,
              type: { in: ["crisis_content", "crisis_post", "crisis_message", "crisis_comment"] },
            },
            take: params.limit,
            orderBy: { detectedAt: "desc" },
          }),
        ]);

        return NextResponse.json({
          crisisReports,
          safetyAlerts,
          requiresImmediate: crisisReports.filter(r => r.severityLevel >= 8).length,
        });
      }

      return NextResponse.json({ error: "Invalid type parameter" }, { status: 400 });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return NextResponse.json(
          { error: "Invalid query parameters", details: error.issues },
          { status: 400 }
        );
      }
      console.error("Error fetching moderation queue:", error);
      return NextResponse.json(
        { error: "Failed to fetch moderation queue" },
        { status: 500 }
      );
    }
  }
);

// POST /api/community/moderation - Create moderation action
export const POST = withRoles(
  [UserRole.ADMIN, UserRole.SUPER_ADMIN, UserRole.HELPER, UserRole.THERAPIST, UserRole.CRISIS_COUNSELOR],
  async (req: AuthenticatedRequest) => {
    try {
      const moderatorId = req.user!.id;
      const moderatorRole = req.user!.role;

      // Parse and validate input
      const body = await (req as any).json();
      const validatedData = moderationActionSchema.parse(body);

      // Check if target user exists
      const targetUser = await prisma.user.findUnique({
        where: { id: validatedData.targetUserId },
        include: {
          AnonymousIdentity: true,
        },
      });

      if (!targetUser) {
        return NextResponse.json(
          { error: "Target user not found" },
          { status: 404 }
        );
      }

      // Check permissions based on action type
      const restrictiveActions = ["ban", "suspend"];
      if (restrictiveActions.includes(validatedData.type) && 
          !["ADMIN", "SUPER_ADMIN"].includes(moderatorRole)) {
        return NextResponse.json(
          { error: "Insufficient permissions for this action" },
          { status: 403 }
        );
      }

      // Prevent self-moderation
      if (validatedData.targetUserId === moderatorId) {
        return NextResponse.json(
          { error: "Cannot moderate yourself" },
          { status: 400 }
        );
      }

      // Get or create anonymous identities for moderation
      let moderatorAnonymousId = await prisma.anonymousIdentity.findUnique({
        where: { userId: moderatorId },
      });

      if (!moderatorAnonymousId) {
        moderatorAnonymousId = await (prisma.anonymousIdentity as any).create({
        data: {
          id: crypto.randomUUID(),
            userId: moderatorId,
            displayName: `Moderator${Math.floor(Math.random() * 10000)}`,
            avatar: `https://api.dicebear.com/7.x/shapes/svg?seed=mod${moderatorId}`,
            colorTheme: "hsl(200, 70%, 50%)",
            trustScore: 1.0,
            badges: ["moderator"],
            languages: ["en"],
            updatedAt: new Date(),
          },
        });
      }

      let targetAnonymousId = targetUser.AnonymousIdentity;
      if (!targetAnonymousId) {
        targetAnonymousId = await (prisma.anonymousIdentity as any).create({
        data: {
          id: crypto.randomUUID(),
            userId: validatedData.targetUserId,
            displayName: `User${Math.floor(Math.random() * 100000)}`,
            avatar: `https://api.dicebear.com/7.x/shapes/svg?seed=${validatedData.targetUserId}`,
            colorTheme: `hsl(${Math.floor(Math.random() * 360)}, 70%, 60%)`,
            trustScore: 0.5,
            badges: [],
            languages: ["en"],
            updatedAt: new Date(),
          },
        });
      }

      // Calculate expiration for temporary actions
      let expiresAt = null;
      if (validatedData.duration) {
        expiresAt = new Date(Date.now() + validatedData.duration * 60 * 60 * 1000);
      }

      // Create moderation action
      const action = await (prisma.moderationAction as any).create({
        data: {
          id: crypto.randomUUID(),
          type: validatedData.type,
          targetUserId: targetAnonymousId!.id,
          moderatorId: moderatorAnonymousId!.id,
          reason: validatedData.reason,
          evidence: validatedData.evidence,
          duration: validatedData.duration,
          expiresAt,
          appealable: validatedData.appealable,
        },
      });

      // Apply the moderation action
      switch (validatedData.type) {
        case "ban":
          await prisma.user.update({
            where: { id: validatedData.targetUserId },
            data: { isActive: false },
          });
          break;

        case "suspend":
          await prisma.user.update({
            where: { id: validatedData.targetUserId },
            data: {
              lockedUntil: expiresAt || new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // Default 7 days
            },
          });
          break;

        case "restrict":
          // Update trust score for restrictions
          await updateTrustScore(validatedData.targetUserId, "negative", 0.3);
          break;

        case "warn":
          // Send warning notification
          await (prisma.notification as any).create({
        data: {
          id: crypto.randomUUID(),
              userId: validatedData.targetUserId,
              type: "moderation_warning",
              title: "Community Guidelines Warning",
              message: validatedData.reason,
              isPriority: true,
              metadata: {
                actionId: action.id,
                appealable: validatedData.appealable,
              },
            },
          });
          break;
      }

      // Handle content-specific actions
      if (validatedData.contentId && validatedData.contentType) {
        switch (validatedData.contentType) {
          case "post":
            await prisma.communityPost.update({
              where: { id: validatedData.contentId },
              data: { isModerated: false },
            });
            break;

          case "comment":
            await prisma.comment.update({
              where: { id: validatedData.contentId },
              data: { isDeleted: true },
            });
            break;
        }
      }

      // Log the moderation action
      await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: moderatorId,
          action: `moderation_${validatedData.type}`,
          resource: "user",
          resourceId: validatedData.targetUserId,
          details: {
            reason: validatedData.reason,
            duration: validatedData.duration,
            contentId: validatedData.contentId,
          },
          outcome: "success",
        },
      });

      return NextResponse.json({
        message: "Moderation action applied successfully",
        action,
      }, { status: 201 });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return NextResponse.json(
          { error: "Invalid input", details: error.issues },
          { status: 400 }
        );
      }
      console.error("Error creating moderation action:", error);
      return NextResponse.json(
        { error: "Failed to create moderation action" },
        { status: 500 }
      );
    }
  }
);

// PUT /api/community/moderation - Review and update moderated content
export const PUT = withRoles(
  [UserRole.ADMIN, UserRole.SUPER_ADMIN, UserRole.HELPER, UserRole.THERAPIST],
  async (req: AuthenticatedRequest) => {
    try {
      const moderatorId = req.user!.id;

      // Parse and validate input
      const body = await (req as any).json();
      const validatedData = reviewContentSchema.parse(body);

      let result: any = {};

      switch (validatedData.contentType) {
        case "post":
          const post = await prisma.communityPost.findUnique({
            where: { id: validatedData.contentId },
          });

          if (!post) {
            return NextResponse.json(
              { error: "Post not found" },
              { status: 404 }
            );
          }

          if (validatedData.action === "approve") {
            result = await prisma.communityPost.update({
              where: { id: validatedData.contentId },
              data: { isModerated: false },
            });

            // Update trust score positively
            if (post.authorId) {
              await updateTrustScore(post.authorId, "positive", 0.1);
            }

          } else if (validatedData.action === "reject") {
            result = await prisma.communityPost.delete({
              where: { id: validatedData.contentId },
            });

            // Update trust score negatively
            if (post.authorId) {
              await updateTrustScore(post.authorId, "negative", 0.2);
            }

          } else if (validatedData.action === "edit" && validatedData.editedContent) {
            result = await prisma.communityPost.update({
              where: { id: validatedData.contentId },
              data: {
                content: validatedData.editedContent,
                isModerated: false,
              },
            });
          }
          break;

        case "comment":
          const comment = await prisma.comment.findUnique({
            where: { id: validatedData.contentId },
          });

          if (!comment) {
            return NextResponse.json(
              { error: "Comment not found" },
              { status: 404 }
            );
          }

          if (validatedData.action === "approve") {
            result = await prisma.comment.update({
              where: { id: validatedData.contentId },
              data: { isModerated: false },
            });

            if (comment.authorId) {
              await updateTrustScore(comment.authorId, "positive", 0.05);
            }

          } else if (validatedData.action === "reject") {
            result = await prisma.comment.update({
              where: { id: validatedData.contentId },
              data: {
                isDeleted: true,
                content: "[This comment has been removed by moderators]",
              },
            });

            if (comment.authorId) {
              await updateTrustScore(comment.authorId, "negative", 0.1);
            }

          } else if (validatedData.action === "edit" && validatedData.editedContent) {
            result = await prisma.comment.update({
              where: { id: validatedData.contentId },
              data: {
                content: validatedData.editedContent,
                isModerated: false,
              },
            });
          }
          break;
      }

      // Log the review action
      await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
          userId: moderatorId,
          action: `review_${validatedData.action}`,
          resource: validatedData.contentType,
          resourceId: validatedData.contentId,
          details: {
            reason: validatedData.reason,
            edited: validatedData.action === "edit",
          },
          outcome: "success",
        },
      });

      return NextResponse.json({
        message: `Content ${validatedData.action}ed successfully`,
        result,
      }, { status: 200 });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return NextResponse.json(
          { error: "Invalid input", details: error.issues },
          { status: 400 }
        );
      }
      console.error("Error reviewing content:", error);
      return NextResponse.json(
        { error: "Failed to review content" },
        { status: 500 }
      );
    }
  }
);

// @ts-nocheck
// Community posts API with CRUD operations and moderation
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import { prisma } from "@/lib/prisma";
import { withAuth, AuthenticatedRequest } from "@/lib/auth-middleware";
import { 
  moderateContent, 
  sanitizeContent, 
  checkContentRateLimit,
  updateTrustScore 
} from "@/lib/community/moderation";
import { z } from "zod";

// Input validation schemas
const createPostSchema = z.object({
  title: z.string().min(1).max(200),
  content: z.string().min(1).max(5000),
  category: z.string().min(1).max(50),
  isAnonymous: z.boolean().optional().default(true),
  tags: z.array(z.string()).optional().default([]),
});

const updatePostSchema = z.object({
  title: z.string().min(1).max(200).optional(),
  content: z.string().min(1).max(5000).optional(),
  category: z.string().min(1).max(50).optional(),
  tags: z.array(z.string()).optional(),
});

const querySchema = z.object({
  page: z.coerce.number().int().positive().optional().default(1),
  limit: z.coerce.number().int().positive().max(100).optional().default(20),
  category: z.string().optional(),
  search: z.string().optional(),
  authorId: z.string().optional(),
  isAnonymous: z.coerce.boolean().optional(),
  sortBy: z.enum(["recent", "popular", "discussed"]).optional().default("recent"),
});

// GET /api/community/posts - Get all posts with pagination and filtering
export async function GET(req: NextRequest) {
  try {
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const params = querySchema.parse({
      page: searchParams.get("page"),
      limit: searchParams.get("limit"),
      category: searchParams.get("category"),
      search: searchParams.get("search"),
      authorId: searchParams.get("authorId"),
      isAnonymous: searchParams.get("isAnonymous"),
      sortBy: searchParams.get("sortBy"),
    });

    const skip = (params.page - 1) * params.limit;

    // Build where clause
    const where: any = {
      isModerated: false, // Don't show posts under moderation
    };

    if (params.category) {
      where.category = params.category;
    }

    if (params.search) {
      where.OR = [
        { title: { contains: params.search, mode: "insensitive" } },
        { content: { contains: params.search, mode: "insensitive" } },
      ];
    }

    if (params.authorId) {
      where.authorId = params.authorId;
    }

    if (params.isAnonymous !== undefined) {
      where.isAnonymous = params.isAnonymous;
    }

    // Build orderBy clause
    let orderBy: any = {};
    switch (params.sortBy) {
      case "popular":
        orderBy = { likeCount: "desc" };
        break;
      case "discussed":
        orderBy = { viewCount: "desc" };
        break;
      case "recent":
      default:
        orderBy = { createdAt: "desc" };
    }

    // Get posts with author info
    const [posts, totalCount] = await Promise.all([
      prisma.communityPost.findMany({
        where,
        skip,
        take: params.limit,
        orderBy,
        include: {
          User: {
            select: {
              id: true,
              displayName: true,
              avatarUrl: true,
              AnonymousIdentity: {
                select: {
                  displayName: true,
                  avatar: true,
                  colorTheme: true,
                },
              },
            },
          },
          _count: {
            select: {
              // Count related comments if we add a Comment model
            },
          },
        },
      }),
      prisma.communityPost.count({ where }),
    ]);

    // Process posts for anonymous authors
    const processedPosts = posts.map(post => {
      const { User, ...postData } = post;

      if (post.isAnonymous && User?.AnonymousIdentity) {
        return {
          ...postData,
          author: {
            displayName: User.AnonymousIdentity.displayName,
            avatar: User.AnonymousIdentity.avatar,
            isAnonymous: true,
          },
        };
      } else if (User) {
        return {
          ...postData,
          author: {
            id: User.id,
            displayName: User.displayName,
            avatar: User.avatarUrl,
            isAnonymous: false,
          },
        };
      } else {
        return {
          ...postData,
          author: {
            displayName: "Anonymous User",
            avatar: null,
            isAnonymous: true,
          },
        };
      }
    });

    return NextResponse.json({
      posts: processedPosts,
      pagination: {
        page: params.page,
        limit: params.limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / params.limit),
      },
    });
  } catch (error) {
    console.error("Error fetching posts:", error);
    return NextResponse.json(
      { error: "Failed to fetch posts" },
      { status: 500 }
    );
  }
}

// POST /api/community/posts - Create a new post
export const POST = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;

    // Check rate limiting
    if (!checkContentRateLimit(userId, 5)) {
      return NextResponse.json(
        { error: "Rate limit exceeded. Please wait before posting again." },
        { status: 429 }
      );
    }

    // Parse and validate input
    const body = await (req as any).json();
    const validatedData = createPostSchema.parse(body);

    // Sanitize content
    const sanitizedTitle = sanitizeContent(validatedData.title);
    const sanitizedContent = sanitizeContent(validatedData.content);

    // Moderate content for safety
    const moderationResult = await moderateContent(
      `${sanitizedTitle} ${sanitizedContent}`,
      userId,
      "post"
    );

    // Handle crisis detection
    if (moderationResult.crisisDetected) {
      // Still allow the post but trigger interventions
      if (moderationResult.crisisLevel === "high") {
        // Create crisis alert
        await (prisma.safetyAlert as any).create({
        data: {
          id: crypto.randomUUID(),
            type: "crisis_post",
            severity: "high",
            userId,
            context: sanitizedTitle,
            indicators: moderationResult.reasons,
            handled: false,
            actions: moderationResult.suggestedActions,
          },
        });
      }
    }

    // Block spam and inappropriate content
    if (moderationResult.spamDetected || moderationResult.inappropriateContent) {
      // Update trust score negatively
      await updateTrustScore(userId, "negative", 0.2);

      return NextResponse.json(
        { 
          error: "Your post has been flagged for review.",
          reasons: moderationResult.reasons,
        },
        { status: 400 }
      );
    }

    // Create anonymous identity if needed
    let anonymousIdentity = null;
    if (validatedData.isAnonymous) {
      anonymousIdentity = await prisma.anonymousIdentity.findUnique({
        where: { userId },
      });

      if (!anonymousIdentity) {
        // Create anonymous identity
        const adjectives = ["Brave", "Hopeful", "Strong", "Caring", "Wise"];
        const nouns = ["Phoenix", "Lighthouse", "Guardian", "Explorer", "Dreamer"];
        const randomAdjective = adjectives[Math.floor(Math.random() * adjectives.length)];
        const randomNoun = nouns[Math.floor(Math.random() * nouns.length)];
        const randomNumber = Math.floor(Math.random() * 1000);

        anonymousIdentity = await (prisma.anonymousIdentity as any).create({
        data: {
          id: crypto.randomUUID(),
            userId,
            displayName: `${randomAdjective}${randomNoun}${randomNumber}`,
            avatar: `https://api.dicebear.com/7.x/shapes/svg?seed=${userId}`,
            colorTheme: `hsl(${Math.floor(Math.random() * 360)}, 70%, 60%)`,
            trustScore: 0.5,
            badges: [],
            languages: ["en"],
            updatedAt: new Date(),
          },
        });
      }
    }

    // Create the post
    const post = await (prisma.communityPost as any).create({
        data: {
          id: crypto.randomUUID(),
        authorId: userId,
        title: sanitizedTitle,
        content: sanitizedContent,
        category: validatedData.category,
        isAnonymous: validatedData.isAnonymous,
        isModerated: moderationResult.requiresReview,
        isPinned: false,
        viewCount: 0,
        likeCount: 0,
        updatedAt: new Date(),
      },
      include: {
        User: {
          select: {
            id: true,
            displayName: true,
            avatarUrl: true,
            AnonymousIdentity: {
              select: {
                displayName: true,
                avatar: true,
                colorTheme: true,
              },
            },
          },
        },
      },
    });

    // Update trust score positively for successful post
    if (!moderationResult.requiresReview) {
      await updateTrustScore(userId, "positive", 0.05);
    }

    // Process post for response
    const { User, ...postData } = post as any;
    const processedPost = validatedData.isAnonymous && User?.AnonymousIdentity
      ? {
          ...postData,
          author: {
            displayName: User.AnonymousIdentity.displayName,
            avatar: User.AnonymousIdentity.avatar,
            isAnonymous: true,
          },
        }
      : {
          ...postData,
          author: {
            id: User?.id,
            displayName: User?.displayName,
            avatar: User?.avatarUrl,
            isAnonymous: false,
          },
        };

    // Send response based on moderation status
    if (moderationResult.requiresReview) {
      return NextResponse.json({
        message: "Your post has been submitted for review and will be visible once approved.",
        post: processedPost,
        moderation: {
          status: "pending_review",
          reasons: moderationResult.reasons,
        },
      }, { status: 201 });
    }

    return NextResponse.json({
      message: "Post created successfully",
      post: processedPost,
      interventions: moderationResult.crisisDetected ? moderationResult.suggestedActions : [],
    }, { status: 201 });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid input", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error creating post:", error);
    return NextResponse.json(
      { error: "Failed to create post" },
      { status: 500 }
    );
  }
});

// PUT /api/community/posts - Update a post
export const PUT = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const postId = searchParams.get("id");

    if (!postId) {
      return NextResponse.json(
        { error: "Post ID is required" },
        { status: 400 }
      );
    }

    // Check if post exists and user is the author
    const existingPost = await prisma.communityPost.findUnique({
      where: { id: postId },
    });

    if (!existingPost) {
      return NextResponse.json(
        { error: "Post not found" },
        { status: 404 }
      );
    }

    if (existingPost.authorId !== userId) {
      return NextResponse.json(
        { error: "You can only edit your own posts" },
        { status: 403 }
      );
    }

    // Parse and validate input
    const body = await (req as any).json();
    const validatedData = updatePostSchema.parse(body);

    // Build update data
    const updateData: any = {};

    if (validatedData.title) {
      updateData.title = sanitizeContent(validatedData.title);
    }

    if (validatedData.content) {
      updateData.content = sanitizeContent(validatedData.content);
    }

    if (validatedData.category) {
      updateData.category = validatedData.category;
    }

    // Moderate updated content
    if (validatedData.title || validatedData.content) {
      const contentToModerate = `${updateData.title || existingPost.title} ${updateData.content || existingPost.content}`;
      const moderationResult = await moderateContent(contentToModerate, userId, "post");

      if (moderationResult.spamDetected || moderationResult.inappropriateContent) {
        return NextResponse.json(
          { 
            error: "Your updates have been flagged for review.",
            reasons: moderationResult.reasons,
          },
          { status: 400 }
        );
      }

      updateData.isModerated = moderationResult.requiresReview;
    }

    // Update the post
    const updatedPost = await prisma.communityPost.update({
      where: { id: postId },
      data: {
        ...updateData,
        updatedAt: new Date(),
      },
    });

    return NextResponse.json({
      message: "Post updated successfully",
      post: updatedPost,
    });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid input", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error updating post:", error);
    return NextResponse.json(
      { error: "Failed to update post" },
      { status: 500 }
    );
  }
});

// DELETE /api/community/posts - Delete a post
export const DELETE = withAuth(async (req: AuthenticatedRequest) => {
  try {
    const userId = req.user!.id;
    const userRole = req.user!.role;
    const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
    const postId = searchParams.get("id");

    if (!postId) {
      return NextResponse.json(
        { error: "Post ID is required" },
        { status: 400 }
      );
    }

    // Check if post exists
    const existingPost = await prisma.communityPost.findUnique({
      where: { id: postId },
    });

    if (!existingPost) {
      return NextResponse.json(
        { error: "Post not found" },
        { status: 404 }
      );
    }

    // Check authorization - author or admin can delete
    const isAuthor = existingPost.authorId === userId;
    const isAdmin = ["ADMIN", "SUPER_ADMIN"].includes(userRole);

    if (!isAuthor && !isAdmin) {
      return NextResponse.json(
        { error: "You don't have permission to delete this post" },
        { status: 403 }
      );
    }

    // Delete the post
    await prisma.communityPost.delete({
      where: { id: postId },
    });

    // Log moderation action if deleted by admin
    if (isAdmin && !isAuthor) {
      await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
          userId,
          action: "delete_post",
          resource: "community_post",
          resourceId: postId,
          details: {
            reason: "Admin action",
            originalAuthor: existingPost.authorId,
          },
          outcome: "success",
        },
      });
    }

    return NextResponse.json({
      message: "Post deleted successfully",
    });

  } catch (error) {
    console.error("Error deleting post:", error);
    return NextResponse.json(
      { error: "Failed to delete post" },
      { status: 500 }
    );
  }
});

// @ts-nocheck
// Content reporting API for community safety
import { NextRequest, NextResponse } from "next/server";
import { generatePrismaCreateFields } from "@/lib/prisma-helpers";
import { prisma } from "@/lib/prisma";
import { withAuth, AuthenticatedRequest, withRoles } from "@/lib/auth-middleware-exports";
import { UserRole } from "@/types/prisma";
import { z } from "zod";

// Add Report model to track reports
const reportSchema = z.object({
  contentType: z.enum(["post", "comment", "message", "user", "chatroom"]),
  contentId: z.string(),
  reason: z.enum([
    "spam",
    "harassment",
    "hate_speech",
    "violence",
    "self_harm",
    "inappropriate",
    "misinformation",
    "privacy_violation",
    "other"
  ]),
  description: z.string().min(10).max(1000),
  urgency: z.enum(["low", "medium", "high", "crisis"]).optional(),
  evidence: z.array(z.string()).optional(),
});

const querySchema = z.object({
  page: z.coerce.number().int().positive().optional().default(1),
  limit: z.coerce.number().int().positive().max(100).optional().default(20),
  status: z.enum(["pending", "reviewing", "resolved", "dismissed"]).optional(),
  contentType: z.enum(["post", "comment", "message", "user", "chatroom"]).optional(),
  urgency: z.enum(["low", "medium", "high", "crisis"]).optional(),
});

// Store reports in memory (in production, add a Report model to Prisma)
interface Report {
  id: string;
  reporterId: string;
  contentType: string;
  contentId: string;
  reason: string;
  description: string;
  urgency: string;
  evidence: string[];
  status: string;
  createdAt: Date;
  reviewedBy?: string;
  reviewedAt?: Date;
  resolution?: string;
}

// Temporary in-memory storage (replace with database)
const reports: Map<string, Report> = new Map();

// GET /api/community/reports - Get reports (moderators only)
export const GET = withRoles(
  [UserRole.ADMIN, UserRole.SUPER_ADMIN, UserRole.HELPER, UserRole.THERAPIST],
  async (req: AuthenticatedRequest) => {
    try {
      const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
      const params = querySchema.parse({
        page: searchParams.get("page"),
        limit: searchParams.get("limit"),
        status: searchParams.get("status"),
        contentType: searchParams.get("contentType"),
        urgency: searchParams.get("urgency"),
      });

      // Filter reports based on parameters
      let filteredReports = Array.from(reports.values());

      if (params.status) {
        filteredReports = filteredReports.filter(r => r.status === params.status);
      }

      if (params.contentType) {
        filteredReports = filteredReports.filter(r => r.contentType === params.contentType);
      }

      if (params.urgency) {
        filteredReports = filteredReports.filter(r => r.urgency === params.urgency);
      }

      // Sort by urgency and date
      const urgencyOrder = { crisis: 0, high: 1, medium: 2, low: 3 };
      filteredReports.sort((a, b) => {
        const urgencyDiff = urgencyOrder[a.urgency as keyof typeof urgencyOrder] - 
                           urgencyOrder[b.urgency as keyof typeof urgencyOrder];
        if (urgencyDiff !== 0) return urgencyDiff;
        return b.createdAt.getTime() - a.createdAt.getTime();
      });

      // Paginate
      const start = (params.page - 1) * params.limit;
      const paginatedReports = filteredReports.slice(start, start + params.limit);

      // Get content details for each report
      const enrichedReports = await Promise.all(
        paginatedReports.map(async (report) => {
          let content = null;
          let reporterInfo = null;

          // Get reporter info (anonymous)
          const reporter = await prisma.user.findUnique({
            where: { id: report.reporterId },
            select: {
              AnonymousIdentity: {
                select: {
                  displayName: true,
                  trustScore: true,
                },
              },
            },
          });

          reporterInfo = reporter?.AnonymousIdentity || { displayName: "Anonymous", trustScore: 0.5 };

          // Get content based on type
          switch (report.contentType) {
            case "post":
              content = await prisma.communityPost.findUnique({
                where: { id: report.contentId },
                select: {
                  id: true,
                  title: true,
                  content: true,
                  authorId: true,
                  createdAt: true,
                },
              });
              break;

            case "comment":
              content = await prisma.comment.findUnique({
                where: { id: report.contentId },
                select: {
                  id: true,
                  content: true,
                  authorId: true,
                  postId: true,
                  createdAt: true,
                },
              });
              break;

            case "user":
              const user = await prisma.user.findUnique({
                where: { id: report.contentId },
                select: {
                  id: true,
                  AnonymousIdentity: {
                    select: {
                      displayName: true,
                      trustScore: true,
                    },
                  },
                },
              });
              content = user?.AnonymousIdentity || { displayName: "User" };
              break;
          }

          return {
            ...report,
            content,
            reporter: reporterInfo,
          };
        })
      );

      // Get statistics
      const stats = {
        total: filteredReports.length,
        pending: reports.size,
        crisis: Array.from(reports.values()).filter(r => r.urgency === "crisis").length,
        todayCount: Array.from(reports.values()).filter(r => {
          const today = new Date();
          return r.createdAt.toDateString() === today.toDateString();
        }).length,
      };

      return NextResponse.json({
        reports: enrichedReports,
        pagination: {
          page: params.page,
          limit: params.limit,
          total: filteredReports.length,
          totalPages: Math.ceil(filteredReports.length / params.limit),
        },
        stats,
      });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return NextResponse.json(
          { error: "Invalid query parameters", details: error.issues },
          { status: 400 }
        );
      }
      console.error("Error fetching reports:", error);
      return NextResponse.json(
        { error: "Failed to fetch reports" },
        { status: 500 }
      );
    }
  }
);

// POST /api/community/reports - Create a new report
export const POST = withRoles([UserRole.USER, UserRole.HELPER, UserRole.THERAPIST, UserRole.CRISIS_COUNSELOR, UserRole.ADMIN, UserRole.SUPER_ADMIN], async (req: AuthenticatedRequest) => {
  try {
    const reporterId = req.user!.id;

    // Parse and validate input
    const body = await (req as any).json();
    const validatedData = reportSchema.parse(body);

    // Check if content exists
    let contentExists = false;
    let contentAuthorId: string | null = null;

    switch (validatedData.contentType) {
      case "post":
        const post = await prisma.communityPost.findUnique({
          where: { id: validatedData.contentId },
        });
        contentExists = !!post;
        contentAuthorId = post?.authorId || null;
        break;

      case "comment":
        const comment = await prisma.comment.findUnique({
          where: { id: validatedData.contentId },
        });
        contentExists = !!comment;
        contentAuthorId = comment?.authorId || null;
        break;

      case "message":
        const message = await prisma.directMessage.findUnique({
          where: { id: validatedData.contentId },
        });
        contentExists = !!message;
        contentAuthorId = message?.senderId || null;
        break;

      case "user":
        const user = await prisma.user.findUnique({
          where: { id: validatedData.contentId },
        });
        contentExists = !!user;
        contentAuthorId = validatedData.contentId;
        break;

      case "chatroom":
        const room = await prisma.chatRoom.findUnique({
          where: { id: validatedData.contentId },
        });
        contentExists = !!room;
        break;
    }

    if (!contentExists) {
      return NextResponse.json(
        { error: "Content not found" },
        { status: 404 }
      );
    }

    // Prevent self-reporting
    if (contentAuthorId === reporterId) {
      return NextResponse.json(
        { error: "Cannot report your own content" },
        { status: 400 }
      );
    }

    // Check for duplicate reports
    const existingReport = Array.from(reports.values()).find(
      r => r.reporterId === reporterId && 
          r.contentId === validatedData.contentId &&
          r.status === "pending"
    );

    if (existingReport) {
      return NextResponse.json(
        { error: "You have already reported this content" },
        { status: 409 }
      );
    }

    // Determine urgency based on reason if not provided
    let urgency = validatedData.urgency;
    if (!urgency) {
      if (["self_harm", "violence", "hate_speech"].includes(validatedData.reason)) {
        urgency = "high";
      } else if (["harassment", "privacy_violation"].includes(validatedData.reason)) {
        urgency = "medium";
      } else {
        urgency = "low";
      }
    }

    // Create report
    const report: Report = {
      id: `report_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      reporterId,
      contentType: validatedData.contentType,
      contentId: validatedData.contentId,
      reason: validatedData.reason,
      description: validatedData.description,
      urgency,
      evidence: validatedData.evidence || [],
      status: "pending",
      createdAt: new Date(),
    };

    reports.set(report.id, report);

    // Handle crisis/urgent reports
    if (urgency === "crisis" || validatedData.reason === "self_harm") {
      // Create safety alert
      await (prisma.safetyAlert as any).create({
        data: {
          id: crypto.randomUUID(),
          type: "user_report",
          severity: urgency,
          userId: contentAuthorId || reporterId,
          context: `${validatedData.contentType} reported for ${validatedData.reason}`,
          indicators: [validatedData.reason],
          handled: false,
          actions: ["Review content immediately", "Check user wellbeing"],
        },
      });

      // Notify crisis counselors
      const counselors = await prisma.user.findMany({
        where: {
          role: UserRole.CRISIS_COUNSELOR,
          isActive: true,
        },
        select: { id: true },
      });

      for (const counselor of counselors) {
        await (prisma.notification as any).create({
        data: {
          id: crypto.randomUUID(),
            userId: counselor.id,
            type: "urgent_report",
            title: "Urgent Content Report",
            message: `A ${validatedData.contentType} has been reported for ${validatedData.reason}`,
            isPriority: true,
            metadata: {
              reportId: report.id,
              contentType: validatedData.contentType,
              reason: validatedData.reason,
            },
          },
        });
      }
    }

    // Notify moderators for high priority reports
    if (["high", "crisis"].includes(urgency)) {
      const moderators = await prisma.user.findMany({
        where: {
          OR: [
            { role: UserRole.ADMIN },
            { role: UserRole.HELPER },
            { role: UserRole.THERAPIST },
          ],
          isActive: true,
        },
        select: { id: true },
        take: 5, // Notify first 5 available moderators
      });

      for (const moderator of moderators) {
        await (prisma.notification as any).create({
        data: {
          id: crypto.randomUUID(),
            userId: moderator.id,
            type: "content_report",
            title: "New Content Report",
            message: `${urgency.toUpperCase()} priority: ${validatedData.contentType} reported for ${validatedData.reason}`,
            isPriority: urgency === "crisis",
            metadata: {
              reportId: report.id,
            },
          },
        });
      }
    }

    // Auto-hide content for certain violations
    const autoHideReasons = ["hate_speech", "violence", "self_harm"];
    if (autoHideReasons.includes(validatedData.reason)) {
      switch (validatedData.contentType) {
        case "post":
          await prisma.communityPost.update({
            where: { id: validatedData.contentId },
            data: { isModerated: true },
          });
          break;

        case "comment":
          await prisma.comment.update({
            where: { id: validatedData.contentId },
            data: { isModerated: true },
          });
          break;
      }
    }

    // Log the report
    await (prisma.auditLog as any).create({
        data: {
          id: crypto.randomUUID(),
        userId: reporterId,
        action: "create_report",
        resource: validatedData.contentType,
        resourceId: validatedData.contentId,
        details: {
          reason: validatedData.reason,
          urgency,
        },
        outcome: "success",
      },
    });

    return NextResponse.json({
      message: "Report submitted successfully",
      reportId: report.id,
      status: report.status,
      urgency,
      autoHidden: autoHideReasons.includes(validatedData.reason),
    }, { status: 201 });

  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: "Invalid input", details: error.issues },
        { status: 400 }
      );
    }
    console.error("Error creating report:", error);
    return NextResponse.json(
      { error: "Failed to create report" },
      { status: 500 }
    );
  }
});

// PUT /api/community/reports - Update report status (moderators only)
export const PUT = withRoles(
  [UserRole.ADMIN, UserRole.SUPER_ADMIN, UserRole.HELPER, UserRole.THERAPIST, UserRole.CRISIS_COUNSELOR],
  async (req: AuthenticatedRequest) => {
    try {
      const moderatorId = req.user!.id;
      const url = (req as any).url || req.nextUrl?.toString();
    const { searchParams } = new URL(url);
      const reportId = searchParams.get("id");

      if (!reportId) {
        return NextResponse.json(
          { error: "Report ID is required" },
          { status: 400 }
        );
      }

      const report = reports.get(reportId);
      if (!report) {
        return NextResponse.json(
          { error: "Report not found" },
          { status: 404 }
        );
      }

      const body = await (req as any).json();
      const { status, resolution } = z.object({
        status: z.enum(["reviewing", "resolved", "dismissed"]),
        resolution: z.string().optional(),
      }).parse(body);

      // Update report
      report.status = status;
      report.reviewedBy = moderatorId;
      report.reviewedAt = new Date();
      if (resolution) {
        report.resolution = resolution;
      }

      reports.set(reportId, report);

      // Notify reporter of resolution
      if (status === "resolved" || status === "dismissed") {
        await (prisma.notification as any).create({
        data: {
          id: crypto.randomUUID(),
            userId: report.reporterId,
            type: "report_update",
            title: "Report Update",
            message: `Your report has been ${status}. ${resolution || ""}`,
            metadata: {
              reportId,
              status,
            },
          },
        });
      }

      return NextResponse.json({
        message: "Report updated successfully",
        report,
      }, { status: 200 });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return NextResponse.json(
          { error: "Invalid input", details: error.issues },
          { status: 400 }
        );
      }
      console.error("Error updating report:", error);
      return NextResponse.json(
        { error: "Failed to update report" },
        { status: 500 }
      );
    }
  }
);

