OptimizedHeroImage.displayName = 'OptimizedHeroImage';

export default OptimizedImage;

'use client';

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  FolderIcon,
  DocumentIcon,
  PhotoIcon,
  VideoCameraIcon,
  MusicalNoteIcon,
  ArchiveBoxIcon,
  CloudArrowUpIcon,
  CloudArrowDownIcon,
  ShareIcon,
  TrashIcon,
  PencilIcon,
  EyeIcon,
  LinkIcon,
  FolderPlusIcon,
  DocumentPlusIcon,
  MagnifyingGlassIcon,
  AdjustmentsHorizontalIcon,
  Bars3Icon,
  Squares2X2Icon,
  ChevronRightIcon,
  ChevronDownIcon,
  XMarkIcon,
  ShieldCheckIcon,
  LockClosedIcon,
  UserGroupIcon,
  CalendarIcon,
  TagIcon,
  ArrowUpTrayIcon,
  ArrowDownTrayIcon,
  ClipboardDocumentIcon,
